function validateInput(email, password) {
    var ok = true;
    if ((email.length < 4 || email.slice(-13) != '@forces.gc.ca') && email != 'amelia.earhart97@outlook.com') {
    //if (email.length < 4){
        setAlert('email', 'Please enter your @forces.gc.ca email address');
        ok = false;
    }
    else {setAlert('email', '')}
    if (password.length < 5 ) {
        setAlert('password','Please enter a password');
        ok = false;
    }
    else {setAlert('password', '')}
    return ok;
}



function handleError(errorCode, errorMessage) {
    switch(errorCode) {
        case 'auth/weak-password':
            setAlert('password','The password is too weak');
            break;
        case 'auth/wrong-password': 
            setAlert('password', 'Wrong password')
            break;
        case 'auth/email-already-in-use': 
            setAlert('email','The email address is already in use by another account.');
            break;
        case 'auth/user-not-found': 
            setAlert('email', 'This user does not exist. Please sign up.');
            break;
        default: 
            setAlert('email', errorMessage);
    }
}
/**
 * Handles the sign up button press.
 */
function signUp() {
    var email = document.getElementById('email').value;
    var password = document.getElementById('password').value;

    if (!validateInput(email, password)) {
        return;
    }
    // Sign in with email and pass.
    // [START createwithemail]
    firebase.auth().createUserWithEmailAndPassword(email, password).catch(function(error) {
        // Handle Errors here.
        handleError(error.code, error.message);
        console.log(error);
        return;
    });
    firebase.auth().onAuthStateChanged(function(user) {
        if (user) {
            if (!user.displayName) {
                var userName = user.email.split(/([0-9]+)/)[0];
                userName = userName.split('@')[0];
                var [first, last] = userName.split('.');
                first = first[0].toUpperCase() + first.substring(1).toLowerCase();
                last = last[0].toUpperCase() + last.substring(1).toLowerCase();
                user.updateProfile({
                    displayName: first + " " + last
                }).catch(function(error) {
                    console.log("An error occurred while creating name. Please try again.");
                });
            }
            if (!user.emailVerified) {
                user.sendEmailVerification();
            }
        }
    });
    var user = firebase.auth().currentUser;
    if (user) {

    }
}

function toggleSignIn() {
    if (firebase.auth().currentUser) {
      // [START signout]
      firebase.auth().signOut();
      // [END signout]
    } else {
        var email = document.getElementById('email').value;
        var password = document.getElementById('password').value;
        if (!validateInput(email, password)) {
            return;
        }
        // Sign in with email and pass.
        firebase.auth().signInWithEmailAndPassword(email, password).catch(function(error) {
            // Handle Errors here.
            handleError(error.code, error.message);
            console.log(error);
        });

        firebase.auth().onAuthStateChanged(function(user) {
            if (user) {
              if (!user.emailVerified) {
                  setAlert('email', 'Please verify your email.')
              }
            }
        });
    }
    //document.getElementById('quickstart-sign-in').disabled = true;
  }

function setAlert(type, alertText) {
    var errorType = type + 'Error';
    document.getElementById(errorType).innerHTML = alertText;
}
function LoginForm() {
    //var pilots = []
    //firebase.ref('/pilots').once('name').then(function(snapshot) {
    //    console.log(snapshot.val())
    //});

    return (
        <form className="form">
            <h2 className="userForm">Email</h2>
            <input id="email" type="text"/>
            <p id="emailError"/>
            <br/>
            <h2 className="pswdForm" >Password</h2>
            <input id="password" type="password"/>
            <p id="passwordError"/>
            <RenderFormButtons/>
        </form> 
    )
}

function RenderFormButtons() {
    return (
        <div className='login-form-btns '>
                <button className="sign-up-btn" type="button" onClick={signUp}>
                    Sign Up
                </button>
                <button className="sign-in-btn" type="button" onClick={toggleSignIn}>
                    Next
                </button>
        </div>
    );
}

class Login extends React.Component{
    constructor(props) {
        super(props)
    }
    render() {
        return (
            <LoginForm/>
        )
    }
}

export default Login;
