import React from 'react';
import './index.css';
import './Dashboard.css';
import firebase from "./Firebase.js";
import ReactGA from 'react-ga';
import moment from 'moment';
import ADD_LOGO from './img/add.png';
import WeekCalendar, { MonthCalendar } from './Calendar.js';
import updateData from './insertDataTest.js';
import ErrorPage from './ErrorPage.js';

Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});
Date.prototype.toDateInputValuePos = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() + this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});

function initializeReactGA() {
    ReactGA.initialize('UA-108277898-5');
    ReactGA.pageview('/Dashboard');
  }

function FlightCard(props) {
    var date = "";
    if (props.data.date == props.today.toDateInputValue()){ date = 'Today';} 
    else if (props.data.date == props.tomorrow.toDateInputValue()) { date = "Tomorrow";} 
    else {var tmp = new Date(props.data.date);
        tmp.setDate(tmp.getDate() + 1);
        date = tmp.toDateString().slice(0,10).replace(' ', ', ');}

    var time = (props.data.timeStart < 100 ? '0' : '') +(props.data.timeStart < 1000 ? '0' : '') + props.data.timeStart + ' - ' + (props.data.timeEnd < 100 ? '0' : '') +(props.data.timeEnd < 1000 ? '0' : '') + props.data.timeEnd;
    return (
        <div className="flightCard" onClick={props.toggleOverlay.bind(this, props.data.flightid)}>
            <div className="date">
                <h2>{date}</h2>
                <p>{time}</p>
            </div>
            <hr/>
            <div className="infoLine">
                <div className="title">
                    <h5>CAPT</h5>
                </div>
                <div className="text">
                    <h5>{props.data.captain[0].toUpperCase() + ". " + props.data.captain[1].toUpperCase() + props.data.captain.slice(2)}</h5>
                </div>
            </div>
            <div className="infoLine">
                <div className="title">
                    <h5>FO</h5>
                </div>
                <div className="text">
                    <h5>{props.data.fo[0].toUpperCase() + ". " + props.data.fo[1].toUpperCase() + props.data.fo.slice(2)}</h5>
                </div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>A/C</h5></div>
                <div className="text"><h5>{props.data.ac}</h5></div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>TYPE</h5></div>
                <div className="text"><h5>{props.data.details}</h5></div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>CONFIG</h5></div>
                <div className="text"><h5>{props.data.config}</h5></div>
            </div>
        </div>
    );
}

function WeekView (props) {
    return (
        <WeekCalendar events={props.events} dates={props.curWeek} curWeekData={props.curWeekData} toggleOverlay={(type,id)=>{props.toggleOverlay(type,id)}}/>
    )
}

function MonthView (props) {
    return (
        <MonthCalendar curWeek={props.curWeek} changeCurWeek={(day)=>{props.changeCurWeek(day)}} id={props.id}/>
    )
}

class Dashboard extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            data: {},
            curDate: new Date(),
            curWeek: this.initWeek(),
            curWeekData: [], 
            hasError: false,
        }
    }

    initWeek() {
        var week = [];
        var cur = new Date();
        var startOfWeek = moment(cur).startOf('isoweek');
        for (var i =0; i < 5; i++) {
            week.push([startOfWeek.month(), startOfWeek.date()]);
            startOfWeek.add(1, 'day');
        }
        return week; 
    }

    changeCurWeek(day) {
        var week = [];
        var startOfWeek = day.startOf('isoweek');
        for (var i =0; i < 5; i++) {
            week.push([startOfWeek.month(), startOfWeek.date()]);
            startOfWeek.add(1, 'day');
        }

        this.setState({curWeek: week});
        this.getData(week);
    }

    getData(week) {
        var id = this.props.id[0].toLowerCase() + this.props.id.split(' ')[1].toLowerCase();
        week.forEach(day => {
            var d = this.state.curDate.getFullYear() + '-' + (day[0] < 9 ? '0' : '') + (day[0]+1) + '-' + (day[1] < 10 ? '0' : '') + day[1] ;

            console.log(d);
            firebase.firestore().collection('dates').doc(d).get()
                .then((querySnapshot) => {
                    var tmp = this.state.curWeekData;
                    tmp.push(querySnapshot.data().events[id]);
                    this.setState({curWeekData: tmp});
                })
                .catch(function(error) {
                    console.log("Error getting documents: ", error);
            });
        })
    }


    renderUpcomingEvents() {
        return (
            <div className="upcomingEvents">
                <div className="weekView">
                    <WeekView curWeek={this.state.curWeek} curWeekData={this.state.curWeekData} toggleOverlay={(type,id)=>{this.props.toggleOverlay(type,id)}}/>
                </div>
                <div className="monthView">
                    <MonthView curWeek={this.state.curWeek} changeCurWeek={(day)=>{this.changeCurWeek(day)}} id={this.props.id} />
                </div>
            </div>
        )
    }

    renderUpcomingFlights() {
        this.initWeek();
        var cards = [];
        var today = new Date(this.state.curDate);
        var tomorrow = new Date(this.state.curDate);
        tomorrow.setDate(today.getDate() + 1);
        for (var i= 0; i < this.state.data.length; i++) {
            cards.push(<FlightCard data={this.state.data[i].data()} today={today} tomorrow={tomorrow} toggleOverlay={(id)=>{this.props.toggleOverlay('flight', id);}}/>)
        }
        return cards
    }

    componentDidMount() {
        //updateData();
        firebase.firestore().collection("pilots").doc('aearhart').collection('upcoming-events').where('date', '>=', this.state.curDate.toDateInputValue())
        .get()
        .then((querySnapshot) => {
            if (!querySnapshot.empty) {
                this.setState({data: querySnapshot.docs})
            }
            else {
                console.log("Document doesn't exist");
            };
        })
        .catch(function(error) {
            console.log("Error getting documents: ", error);
        });

        this.getData(this.state.curWeek);

        initializeReactGA();
    }

    componentDidCatch(error, info) {
        // Display fallback UI
        this.setState({ hasError: true });
        console.log(error, info);
    }

    render() { 
        if (this.state.hasError) {
            return <ErrorPage/>
          }
        return (
            <div>
                <h2>My Upcoming Flights</h2>
                <div className="upcomingFlights">
                    {this.renderUpcomingFlights()}
                </div>
                <h2>My Schedule</h2>
                {this.renderUpcomingEvents()}
            </div>

        )
    };
}

export default Dashboard;
