import React from 'react';
import moment from 'moment'
import firebase from "./Firebase.js";
import './Dashboard.css';
import RIGHT_BTN from './img/right-arrow.png';
import LEFT_BTN from './img/left-arrow.png';

function DayHeader(props) {
    return (

        <div className='dayHeader'>
            <h3 id='date'>{props.day + "  "}</h3><h3>{props.date}</h3>
        </div>
    )
}

function DayContents(props) {
    var content = [];
    if (props.curDayData) {
        props.curDayData.forEach(event => {
            var [type, start, end, details] = [event.type, event.startTime, event.endTime, event.title];
            var h = Math.max(end-start, 60);
            content.push(
                <div key={event.type+event.startTime+event.endTime} className={type+'Event'} style={{height: (h*0.6)+'px'}} onClick={()=>{props.toggleOverlay(type, event.id)}}>
                    <p>{start + " - " +end}</p>
                    <h5 className='name'>{type[0].toUpperCase()+type.slice(1,)}</h5>
                    <h6>{details}</h6>
                </div>
            )
        });
    }
    return content;
}

function DayView(props) {
    return (
        <div className='dayView'>
            <DayHeader day={props.day} date={props.date}/>
            <DayContents events={props.events} curDayData={props.curDayData} toggleOverlay={(type,id)=>{props.toggleOverlay(type,id)}}/>
        </div>
    )
}

function WeekdaysHeader() {
    var weekdayShort = moment.weekdaysShort();
    var content = []
    for (var i = 0; i < 7; i++) {
        content.push(<th key={weekdayShort[i]} className="week-day">
        {weekdayShort[i]}
       </th>);
    }

    return content;
}



function DaysOfMonth(props) {
    var m = props.displayed.format("MM");
    var y = props.displayed.format("YYYY");

    var blanks = [];
    for (let i = 0; i < props.firstDayOfMonth; i++) {
      blanks.push(
        <td key={'blanks'+i} className="calendar-day empty">{""}</td>
      );
    }
    var parsedWeek = {firstDay: props.curWeek[0][1], month: props.curWeek[0][0], doesOverlap: (props.curWeek[0][1]>22 && props.curWeek[4][1] < 6), days: []};
    props.curWeek.forEach(a => {parsedWeek.days.push(a[1])});
    var daysInMonth = [];
    var displayedMonth = props.displayed.month();

    for (let d = 1; d <= props.daysInMonth; d++) { 
        var currentDay = "";   
        if (displayedMonth == parsedWeek.month && parsedWeek.days.includes(d)) {
            currentDay = "curweek";
        }
        var testDate = y + "-" + m + "-" + (d < 10 ? '0':'') + d;
        var dot = props.flights && props.flights[testDate] == true ? 'cal-dot hasFlight' : 'cal-dot';
        daysInMonth.push(  
            <td key={d} className={`calendar-day ${currentDay}`}>   
                <span onClick={e => { props.changeCurWeek(moment().month(displayedMonth).date(d));}} >{d}</span>
                <div className={dot}></div>
            </td>);
    }
    var totalSlots = [...blanks, ...daysInMonth];
    let rows = [];
    let cells = [];

    totalSlots.forEach((row, i) => {
        if (i % 7 !== 0 ) {
          cells.push(row); // if index not equal 7 that means not go to next week
        } else {
            rows.push(cells); // when reach next week we contain all td in last week to rows : we're excluding weekends
            cells = []; // empty container 
            cells.push(row); // in current loop we still push current row to new container
        }
        if (i === totalSlots.length - 1) { // when end loop we add remain date
          rows.push(cells);
        }
    });

    let daysinmonth = rows.map((d, i) => {
    return <tr key={'row'+i}>{d}</tr>;
    });

    return (
        <table className="calendar-table">
        <thead>
            <tr>{WeekdaysHeader()}</tr>
        </thead>
        <tbody>{daysinmonth}</tbody>
        </table>
    )
}

class Calendar extends React.Component {
    constructor(props) {
        super(props); 
            /*  curDate : 
            *   id : 
            *   showWeekends : bool
            *   toggleOverlay()
            */
        this.state = {
            displayed: moment(), 
            flights: {},
            curWeekData: [],
        }
    }

    firstDayOfMonth = () => {
        let dateObject = this.state.displayed;
        let firstDay = moment(dateObject)
                     .startOf("month")
                     .format("d"); 
       return firstDay;
    };

    month = () => {
        return this.state.displayed.format("MMMM");};
    year = () => {
        return this.state.displayed.format("YYYY");
    }

    onPrev = () => {
        this.setState({displayed: this.state.displayed.subtract(1, 'month')});
    };
    onNext = () => {
        this.setState({displayed: this.state.displayed.add(1, 'month')});
    };

    componentDidMount() {
        //Convert displayName to userID
        var id = this.props.id[0].toLowerCase() + this.props.id.split(' ')[1].toLowerCase();

        //Retrieving data
        firebase.firestore().collection('pilots').doc(id).get()
        .then((querySnapshot) => {
            this.setState({flights: querySnapshot.data().flights})
        })
        .catch(function(error) {
            console.log("Error getting documents: ", error);
        });
    }

    getWeekData(weekDates) {
        var id = this.props.id[0].toLowerCase() + this.props.id.split(' ')[1].toLowerCase();
        //this.setState({curWeekData: []});
        weekDates.forEach(day => {
            var d = this.props.curDate.getFullYear() + '-' + (day[0] < 9 ? '0' : '') + (day[0]+1) + '-' + (day[1] < 10 ? '0' : '') + day[1] ;
            firebase.firestore().collection('dates').doc(d).get()
                .then((querySnapshot) => {
                    var tmp = this.state.curWeekData;
                    tmp.push(querySnapshot.data().events[id]);
                    this.setState({curWeekData: tmp});
                })
                .catch(function(error) {
                    console.log("Error getting documents: ", error);
            });
        })
    }

    render () {

        var firstDay = this.firstDayOfMonth();
        var daysInMonth = this.state.displayed.daysInMonth();

        var startOfWeek = this.state.showWeekends ? this.state.displayed.startOf('week') : this.state.displayed.startOf('isoweek');
        var dayCount = this.state.showWeekends ? 7 : 5;
        var week = [];
        for (var i =0; i < dayCount; i++) {
            week.push([startOfWeek.month(), startOfWeek.date()]);
            startOfWeek.add(1, 'day');
        }
        this.getWeekData(week);

        var days = this.props.showWeekends ? ['Sun', 'Mon', 'Tues', 'Wed', 'Thur', 'Fri', 'Sat'] : ['Mon', 'Tues', 'Wed', 'Thur', 'Fri'];
        var weekContent = [];

        for (var i=0; i < days.length; i++) {
            weekContent.push(<DayView key={week[i][1]} day={days[i]} date={week[i][1]} curDayData={this.state.curWeekData[i]} toggleOverlay={(type,id)=>{this.props.toggleOverlay(type,id)}}/>)
        }

        return (
            <div>
                <div className="weekView">
                    {weekContent}
                </div>
                <div className="monthView">
                    <div className="month-calendar">
                        <img className='month-btn' src={LEFT_BTN} onClick={e => {this.onPrev();}}></img>
                        <div className="month-calendar-header">{this.month() + " " + this.year()}</div>
                        <img className='month-btn' src={RIGHT_BTN} onClick={e => {this.onNext();}}></img>
                    </div>
                    <DaysOfMonth curWeek={week} 
                                changeCurWeek={(day)=>{this.props.changeCurWeek(day)}} 
                                displayed={this.state.displayed} 
                                firstDayOfMonth={firstDay} 
                                daysInMonth={daysInMonth}
                                flights={this.state.flights}/>
                </div>
            </div>)
    }
}

export default Calendar;
