import React from 'react';
import Dashboard from './Dashboard.js';
import OverlayFlightForm from './OverlayFlightForm.js';
import './App.css';
import DASHBOARD_LOGO from './img/dashboard.png'
import DASHBOARD_ACTIVE_LOGO from './img/dashboard_blue.png'
import FLIGHTS_LOGO from './img/plane.png';
import FLIGHTS_ACTIVE_LOGO from './img/plane_blue.png';
import SCHEDULE_LOGO from './img/calendar.png';
import SCHEDULE_ACTIVE_LOGO from './img/calendar_blue.png';
import ADD_LOGO from './img/add.png';
import ADD_ACTIVE_LOGO from './img/add_blue.png';

function Header(props) {
  return <h1>{props.activeTab}</h1>
}

function NavBarTab(props) {
    const logos = {"Dashboard": Dashboard_LOGO, "Flights": Flights_LOGO}
    return (
      <div className={props.active ? 'navBarTab activeTab' :'navBarTab'}
        onClick={props.onClick}>
        <img className='icon' src={props.icon} alt=""/>
      </div>
    )
}

class NavBar extends React.Component {

  constructor(props) {
    super(props);
  }

  renderNavBarTab(tabName) {
    const logos = {"Dashboard": DASHBOARD_LOGO, "Flights": FLIGHTS_LOGO, "Schedule": SCHEDULE_LOGO, "Add": ADD_LOGO}
    const activelogos = {"Dashboard": DASHBOARD_ACTIVE_LOGO, "Flights": FLIGHTS_ACTIVE_LOGO, "Schedule": SCHEDULE_ACTIVE_LOGO, "Add": ADD_ACTIVE_LOGO}
    const logo = this.props.activeTab === tabName ? activelogos[tabName] : logos[tabName];
    return <NavBarTab tabName={tabName} icon={logo} openNavBar={this.props.openNavBar} onClick={() => {this.props.changeTab(tabName)}}/>
  }

  render() {
    /** const tabs = '';
    this.state.navTabs.forEach(tab => {
      tabs = tabs.concat(this.renderNavBarTab(tab));
    });*/
    return (
      <div className="navBar">
        <div className='navBarTabs'>
          {this.renderNavBarTab('Dashboard')}
          {this.renderNavBarTab('Flights')}
        </div>
      </div>
    );
  }
}

class AppBody extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeTab: "Dashboard",
      navTabs: ["Dashboard", "Flights"],
      flights: ["Thurs, May 2", "Fri, May 4"],
      showOverlay: false,
    };
  }

  renderNavBar() {
    return <NavBar
      activeTab={this.state.activeTab} 
      navTabs={this.state.navTabs} 
      changeTab={(tabName)=>{this.changeTab(tabName)}}/>
  }

  renderHeader() {
    return <Header activeTab = {this.state.activeTab}/>
  }

  renderOverlay(page) {
    var content = ""
    if (this.state.showOverlay) {
      switch(page) {
        case 'OverlayFlightForm': 
          content = <OverlayFlightForm/>
          break;
        default: 
          content = <OverlayFlightForm onClose={()=>{this.toggleOverlay()}}/>
      }
    }

    return (
      <div className = {this.state.showOverlay ? 'overlay' : null}>
        <div className = {this.state.showOverlay ? 'overlayContent' : null}>
          {content}
        </div>
      </div>);
  }

  shouldComponentUpdate(nextState) {
    return this.state.activeTab !== nextState.activeTab;
  }

  toggleOverlay() {
    const currentState = this.state.showOverlay;
    this.setState({showOverlay: !currentState});
  }

  changeTab(tabName) {
    this.setState({activeTab: tabName});
    console.log("Horray! New state is: " + tabName);
  }

  renderContent(tabName) {
    switch(tabName) {
      case 'Flights': 
        break;
      default: 
        return <Dashboard flights={this.state.flights} toggleOverlay={()=>{this.toggleOverlay()}}/>
    }
  }


  render() {
    return (
      <div className="App">
        {this.renderOverlay()}
        <div className="wrapper">
          <div className="header">
            {this.renderHeader()}
          </div>
          {this.renderNavBar()} 
          <div className="content"> 
            {this.renderContent(this.state.activeTab)} 
          </div>
        </div>   
      </div>
    );
  };
}

function App() {
  return (
    <AppBody/>
  );
}

export default App;
export {Header};
