import XLSX from 'xlsx';
import firebase from "./Firebase.js";

const flightHeaders = ["startTime","endTime","ac","captain","config","fo","fe","crew","details","backup","mission","details","config","remarks","pri","meal","dco"];
function s2ab(s) {
    var buf = new ArrayBuffer(s.length);
    var view = new Uint8Array(buf);
    for (var i=0; i<s.length; i++) view[i] = s.charCodeAt(i) & 0xFF;
    return buf;
}

const download = (url, name) => {
    let a = document.createElement('a')
    a.href = url
    a.download = name
    a.click()

    window.URL.revokeObjectURL(url)
}

function getFlightsFromID(ids) {
console.log("ids: ", ids)
    var flights = flightHeaders;
    ids.forEach((id) => {
        firebase.firestore().collection("events").doc(id)
            .get()
            .then((querySnapshot) => {
                var d = querySnapshot.data();
                flights.push([
                    d.startTime,
                    d.endTime,
                    d.ac,
                    d.captain,
                    d.config,
                    d.fo,
                    d.fe,
                    d.crew,
                    d.details,
                    d.backup,
                    d.mission,
                    d.details,
                    d.config,
                    d.remarks,
                    d.pri,
                    d.meal,
                    d.dco,]);

                    return flights;
            })
            .catch(function(error) {
                console.log("Error getting data: ", error);
            });
    })

}

function Export(ids, dayInfo) {
    //Gathering all data: 
    var data = getFlightsFromID(ids);
    /* convert from array of arrays to workbook */
    var worksheet = XLSX.utils.aoa_to_sheet(data);
    var new_workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(new_workbook, worksheet, "ExportedFile");


    /* write file */
    const wbout = XLSX.write(new_workbook, {type:'binary', bookType:"xlsx"});

    let url = window.URL.createObjectURL(new Blob([s2ab(wbout)], {type:'application/octet-stream'}))

    download(url, 'DailyFlyingOrder.xlsx')
}

export default Export;
