import XLSX from 'xlsx';

function s2ab(s) {
    var buf = new ArrayBuffer(s.length);
    var view = new Uint8Array(buf);
    for (var i=0; i<s.length; i++) view[i] = s.charCodeAt(i) & 0xFF;
    return buf;
}

const download = (url, name) => {
    let a = document.createElement('a')
    a.href = url
    a.download = name
    a.click()

    window.URL.revokeObjectURL(url)
}

function Export() {

    var data = [['lafsijoda', 'gsijaofafs', 'gsaijofa'],['gajiopg', 'gsigdsjiogsafafs', 'wnqr'],['gdsgdsgssijoda', 'gsijaofafs', 'gsaijofa']];
    /* convert from array of arrays to workbook */
    var worksheet = XLSX.utils.aoa_to_sheet(data);
    var new_workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(new_workbook, worksheet, "ExportedFile");


    /* write file */
    const wbout = XLSX.write(new_workbook, {type:'binary', bookType:"xlsx"});

    let url = window.URL.createObjectURL(new Blob([s2ab(wbout)], {type:'application/octet-stream'}))

    download(url, 'DailyFlyingOrder.xlsx')
}

export default Export;
