import React from"react";

const Helper = {
    parseID(id) {
        //aearhart to A. Earhart 
        if (id) {
        return id[0].toUpperCase() + ". " + id[1].toUpperCase() + id.slice(2);}
    },

    parseName(name) {
        //Amelia Earhart to aearhart
        if (name) {
        return name[0].toLowerCase() + name.slice(name.indexOf(' ')+1).toLowerCase();}
    },

    getLastName(id) {
        //aearhart to Earhart
        if (id != "") {
            return id[1].toUpperCase() + id.slice(2);
        }
        return "";
    },    

    sortEvents(events) {
        console.log(events);
        var sortedEvents = []
        events.forEach((event) => {
            if (events.length == 0) {
                events.push(event)
            }
            else {
                var i = 0;
                while (i < sortedEvents.length && sortedEvents[i].startTime > events.startTime ) {
                    i += 1;
                }
                //Insert at index i
                sortedEvents.splice(i, 0, event);
            }
        })
        return sortedEvents
    },


    getOptionsList(id, values) {
        //Creates a datalist object for dropdown options
        var l = [];
        values.forEach((v) => {
            l.push(<option value={v}/>);
        });
        return (<datalist id={id}>{l}</datalist>);
    },


    checkFieldError(field, value, acceptedValues, flag) {
        var ok = true;
        switch (field){
            case 'captain':
                if (!acceptedValues.includes(value) && value != "") {
                    flag('captain', 'Invalid pilot');
                    ok = false;
                }
                else {ok = true;}
                break;
            case 'fe':
                if (!acceptedValues.includes(value) && value != "") {
                    flag('fe', 'Invalid pilot');
                    ok = false;
                }
                else {ok = true;}
                break;
            case 'fo':
                if (!acceptedValues.includes(value) && value != "") {
                    flag('fo', 'Invalid pilot');
                    ok = false;
                }
                else {ok = true;}
                break;
            case 'crew':
                if (!acceptedValues.includes(value) && value != "") {
                    flag('crew', 'Invalid pilot');
                    ok = false;
                }
                else {ok = true;}
                break;
            case 'startTime':
                if (isNaN(value) || !this.props.timeValid(value)) {
                    flag('startTime', 'Enter between 0000 and 2359');
                    ok = false;
                }
                else if (parseInt(value) >= parseInt(this.state.endTime)) {
                    flag('endTime', 'Return must be after dep.');
                    ok = false;
                }
                else {ok = true;}
                break;
            case 'endTime':
                if (isNaN(value) || !this.props.timeValid(value)) {
                    flag('endTime', 'Enter between 0000 and 2359');
                    ok = false;
                }
                else if (parseInt(this.state.startTime) >= parseInt(value)) {
                    flag('endTime', 'Return must be after dep.');
                    ok = false;
                }
                else {ok = true;}
                break;
            case 'dco':
                if (!(dcolist.includes(value.toUpperCase()))) {
                    flag('dco', 'Must be DCO/DNCO/DPCO');
                    ok = false;
                }
                else {ok = true;}
                break;
            case 'config':
                if (!(configlist.includes(value.toUpperCase()))) {
                    flag('config', 'Config not accepted. See dropdown.');
                    ok = false;
                }
                else {ok = true;}
                break;
            case 'ac':
                if (!(aclist.includes(value.toUpperCase()))) {
                    flag('ac', 'AC not valid');
                    ok = false;
                }
                else {ok = true;}
                break;
            case 'backup':
                if (!(aclist.includes(value.toUpperCase())) && value != '') {
                    flag('backup', 'Backup not valid');
                    ok = false;
                }
                else {ok = true;}
                break;
            case 'pri':
                if (!["TASK", '1', '2', '3', '4', '5', '6'].includes(value.toUpperCase())) {
                    flag('pri', 'Must be task or 1-6');
                    ok = false;
                }
                else {ok = true;}
                break;
            default: 
                this.setState({ok: true});
                break;
            }
        return ok;
    },

    timeValid(time) {
        return time.length === 4 && parseInt(time.slice(0,2)) < 24 && parseInt(time.slice(2)) < 60;
    },


    flag(field, message) {
        var element = document.getElementById(field);
        element.setAttribute('data-validate', message);
        element.classList.add('alert-validate');
    },
}




export default Helper;
