import React from "react";
import "./Overlay.css";
import $ from 'jquery';


Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});


class OverlayFlightForm extends React.Component {
    constructor(props) { 
        super(props);
    }

    componentDidMount() {

        $(document).ready(function () {
            /*==================================================================
            [ Focus Contact2 ]*/
            $('.input2').each(function(){
                $(this).on('blur', function(){
                    if($(this).val().trim() != "") {
                        $(this).addClass('has-val');
                    }
                    else {
                        $(this).removeClass('has-val');
                    }
                })
            })

            /*==================================================================
            [ Validate ]*/
            var name = $('.validate-input input[name="name"]');
            var email = $('.validate-input input[name="email"]');
            var message = $('.validate-input textarea[name="message"]');


            $('.validate-form').on('submit',function(){
                var check = true;

                if($(name).val().trim() == ''){
                    showValidate(name);
                    check=false;
                }


                if($(email).val().trim().match(/^([a-zA-Z0-9_\-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([a-zA-Z0-9\-]+\.)+))([a-zA-Z]{1,5}|[0-9]{1,3})(\]?)$/) == null) {
                    showValidate(email);
                    check=false;
                }

                if($(message).val().trim() == ''){
                    showValidate(message);
                    check=false;
                }

                return check;
            });


            $('.validate-form .input2').each(function(){
                $(this).focus(function(){
                hideValidate(this);
            });
            });

            function showValidate(input) {
                var thisAlert = $(input).parent();

                $(thisAlert).addClass('alert-validate');
            }

            function hideValidate(input) {
                var thisAlert = $(input).parent();

                $(thisAlert).removeClass('alert-validate');
            }
        });

    }

    render() {
        return (
            <div>
                <form className="form validate-form">
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper validate-input" data-validate="Date is required">
                            <input className="input2 has-val" type="date" name="name" value={new Date().toDateInputValue()}/>
                            <span className="focus-input2" data-placeholder="DATE"></span>
                        </div>
                        <div className="medium-wrapper validate-input" data-validate="ESP is required">
                            <input className="input2" type="text" name="esp"/>
                            <span className="focus-input2" data-placeholder="EST. DEPARTURE"></span>
                        </div>
                        <div className="medium-wrapper validate-input" data-validate="ESR is required">
                            <input className="input2" type="text" name="esr"/>
                            <span className="focus-input2" data-placeholder="EST. RETURN"></span>
                        </div>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper validate-input" data-validate="ESP is required">
                            <input className="input2" type="text" name="esp"/>
                            <span className="focus-input2" data-placeholder="CAPTAIN"></span>
                        </div>
                        <div className="medium-wrapper validate-input" data-validate="ESP is required">
                            <input className="input2" type="text" name="esp"/>
                            <span className="focus-input2" data-placeholder="FIRST OFFICER"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className="input2" type="text" name="esr"/>
                            <span className="focus-input2" data-placeholder="FLIGHT ENGINEER"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className="input2" type="text" name="esr"/>
                            <span className="focus-input2" data-placeholder="OTHER CREW"></span>
                        </div>
                    </div>
                    <div className="row-wrapper">

                        <div className="small-wrapper" data-validate="A/C is required">
                            <input className="input2" type="text" name="A/C"/>
                            <span className="focus-input2" data-placeholder="A/C"></span>
                        </div>
                        <div className="small-wrapper">
                            <input className="input2" type="text" name="BACKUP"/>
                            <span className="focus-input2" data-placeholder="BACKUP"></span>
                        </div>
                        <div className="medium-wrapper" data-validate="Mission is required">
                            <input className="input2" type="text" name="MISSION"/>
                            <span className="focus-input2" data-placeholder="MISSION"></span>
                        </div>
                        <div className="medium-wrapper" data-validate="Details is required">
                            <input className="input2" type="text" name="DETAILS"/>
                            <span className="focus-input2" data-placeholder="DETAILS"></span>
                        </div>
                        <div className="medium-wrapper" data-validate="Config is required">
                            <input className="input2" type="text" name="CONFIG"/>
                            <span className="focus-input2" data-placeholder="CONFIG"></span>
                        </div>
                    </div>
                    <div className="wrap-input2 validate-input" data-validate="Name is required">
                        <input className="input2" type="text" name="remarks"/>
                        <span className="focus-input2" data-placeholder="REMARKS"></span>
                    </div>


                    <div className="half-col-wrapper">
                        <div className="medium-wrapper" data-validate="Name is required">
                            <input className="input2" type="text" name="name"/>
                            <span className="focus-input2" data-placeholder="MEALS"></span>
                        </div>
                        <div className="medium-wrapper" data-validate="Name is required">
                            <input className="input2" type="text" name="name"/>
                            <span className="focus-input2" data-placeholder="DCO/DPCO/DNCO"></span>
                        </div>
                    </div>




                    <div className="container-form-btn">
                        <div className="wrap-form-btn">
                            <div className="form-bgbtn"></div>
                            <button className="form-btn">
                                Add Flight
                            </button>
                        </div>
                    </div>
                </form>

            </div>

        );
    }
}

export default OverlayFlightForm;
