import React from 'react';
import Dashboard from './Dashboard.js'
import './App.css';
import Flights_LOGO from './img/plane.png';
import Dashboard_LOGO from './img/dashboard.png'

function NavBarIcon(props) {
  return (
      <div 
          className={props.open ? 'navBarIcon open': "navBarIcon"} 
          onClick={props.onClick} >
          <div className="bar1"></div>
          <div className="bar2"></div>
          <div className="bar3"></div>
      </div>
  )
}

function Header(props) {
  return <h1>{props.activeTab}</h1>
}

function NavBarTab(props) {
    const logos = {"Dashboard": Dashboard_LOGO, "Flights": Flights_LOGO}
    return (
      <div className={props.active ? 'navBarTab activeTab' :'navBarTab'}
        onClick={props.onClick}>
        <img className='icon' src={logos[props.tabName]} alt=""/>
        <a className={props.open ? null : 'hidden'} href="#">{props.tabName} </a>
      </div>
    )
}

class NavBar extends React.Component {

  constructor(props) {
    super(props);
  }

  renderNavBarIcon() {
    return <NavBarIcon open={this.props.open} onClick={()=> {this.props.toggleOpenNavBar()}}/>;
  }

  renderNavBarTab(tabName) {
    return <NavBarTab tabName={tabName} active={this.props.activeTab === tabName} open={this.props.open} onClick={() => {this.props.changeTab(tabName)}}/>
  }

  render() {
    /** const tabs = '';
    this.state.navTabs.forEach(tab => {
      tabs = tabs.concat(this.renderNavBarTab(tab));
    });*/
    return (
      <div className={this.props.open ? 'openedNavBar navBar': "navBar"}>
        <div className="navBarIcon">
          {this.renderNavBarIcon()}
        </div>
        <div className='navBarTabs'>
          {this.renderNavBarTab('Dashboard')}
          {this.renderNavBarTab('Flights')}
        </div>
      </div>
    );
  }
}

class AppBody extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      activeTab: "Dashboard",
      navTabs: ["Dashboard", "Flights"],
      flights: ["Thurs, May 2", "Fri, May 4"],
      showOverlay: false,
    };
  }

  renderNavBar() {
    return <NavBar
      open={this.state.open} 
      activeTab={this.state.activeTab} 
      navTabs={this.state.navTabs} 
      changeTab={(tabName)=>{this.changeTab(tabName)}}
      toggleOpenNavBar={() => {this.toggleOpenNavBar()}}/>
  }

  renderHeader() {
    return <Header activeTab = {this.state.activeTab}/>
  }

  renderOverlay() {
    return <div className = {this.state.showOverlay ? 'visible' : null}/>
  }

  shouldComponentUpdate(nextState) {
    return this.state.open !== nextState.open;
  }

  toggleOpenNavBar() {
    const currentState = this.state.open;
    this.setState({ open: !currentState });
  };

  toggleOverlay() {
    const currentState = this.state.showOverlay;
    this.setState({showOverlay: !currentState});
  }

  changeTab(tabName) {
    this.setState({activeTab: tabName});
    console.log("Horray! New state is: " + tabName);
  }

  renderContent(tabName) {
    switch(tabName) {
      case 'Flights': 
        break;
      default: 
        return <Dashboard flights={this.state.flights} toggleOverlay={()=>{this.toggleOverlay()}}/>
    }
  }

  render() {
    return (
      <div className="App">
        {this.renderOverlay()}
        <div className="wrapper">
          <div className="header">
            {this.renderHeader()}
          </div>
          <div className="content"> 
            {this.renderContent(this.state.activeTab)} 
          </div>
        </div>
        {this.renderNavBar()}    
      </div>
    );
  };
}

function App() {
  return (
    <AppBody/>
  );
}

export default App;
