import React from "react";
import "./Form.css";
import $ from 'jquery';


Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});

const fields = [[{name: "date", custom: true, size: 'medium', required: true}, 
{name: "esd", displayName: "est. departure", size: 'medium', required: true}, 
{name: "esr", displayName: "est. return", size: 'medium', required: true}, 
{name: "flighttime", custom: true}],

[{name: "captain", size: 'medium', required: true}, 
{name: "fo", displayName: "first officer", size: 'medium', required: true}, 
{name: "fe", displayName: "flight engineer", size: 'medium', required: false}, 
{name: "othercrew", displayName: "other crew", size: 'medium', required: false}], 

[{name: "a/c", size: 'small', required: true}, 
{name: "backup", size: 'small', required: false}, 
{name: "mission", size: 'medium', required: true}, 
{name: "details", size: 'medium', required: true}, 
{name: "config", size: 'medium', required: true}], 
[{name: "remarks", size: 'large', required: false}], 
[{name: "sqnpriority", custom: true, }, 
{name: "meals", size: 'medium', required: false},
{name: "dco/dpco/dnco", size: 'medium', required: true}] ];

function RenderButton(props) {
    return (
        <div className="container-form-btn">
            <div className="wrap-form-btn">
                <div className="form-bgbtn"></div>
                <button className="form-btn" onClick={props.onSubmit}>
                    Add Flight
                </button>
            </div>
        </div>
    );
}


class FlightForm extends React.Component {
    constructor(props) { 
        super(props);
        this.state = {
            date: "",
            esd: "",
            esr: "",
            flighttime: "",
            captain: "",
            fo: "",
            fe: "",
            othercrew: "",
            ac: "",
            backup: "",
            mission: "",
            details: "",
            config: "",
            remarks: "",
            sqnpriority: "",
            meals: "",
            dco: "" 
        }
    }



    componentDidMount() {

        $(document).ready(function () {
            /*==================================================================

            /*==================================================================
            [ Get Data ]*/

            $('.input').on('submit',function(){
                console.log($(this).val());
            });


            /*==================================================================
            [ Validate ]*/
            var name = $('.validate-input input[name="name"]');


            $('.validate-form').on('submit',function(){
                var check = true;

                if($(name).val().trim() == ''){
                    showValidate(name);
                    check=false;
                }

                return check;
            });


            $('.validate-form .input').each(function(){
                $(this).focus(function(){
                hideValidate(this);
            });
            });

            function showValidate(input) {
                var thisAlert = $(input).parent();

                $(thisAlert).addClass('alert-validate');
            }

            function hideValidate(input) {
                var thisAlert = $(input).parent();

                $(thisAlert).removeClass('alert-validate');
            }
        });

    }

    onSubmit() {
        console.log("Horray");
        return;
    }

    _onBlur(val, name) {
        this.setState({[name]: val.trim()}) 
        console.log(val, name)
    }

    renderField(field) {
        const name = field.name;
        const displayName = field.hasOwnProperty("displayName") ? field.displayName.toUpperCase() : field.name.toUpperCase();
        const className = field.size + "-wrapper validate-input" 
        return (
            <div className={className}>
                <input className={"input"+(this.state[name] != ""? " has-val": "")} type="text" name={name} onBlur={(e, name)=>{this._onBlur(e.target.value, name)}}/>
                <span className="focus-input" data-placeholder={displayName}></span>
            </div>
        )
    }

    renderCustomField(field) {
        switch(field.name) {
            case "flighttime": 
                return (<p>Hello</p>);
            case "sqnpriority": 
                return (<p>Hello</p>);
            case "date": 
                return (<div className="medium-wrapper validate-input" data-validate="Date is required">
                <input className="input has-val" type="date" name="name" value={new Date().toDateInputValue()}/>
                <span className="focus-input" data-placeholder="DATE"></span>
            </div>)
            default: 
                return (<p>Hello</p>);
        }
    }

    renderRow(row) {
        var fields = [];
        row.forEach(field => {
            if (field.hasOwnProperty('custom')) {
                fields.push(this.renderCustomField(field));
            }
            else {
                fields.push(this.renderField(field));
            }

        });
        return (
            <div className="row-wrapper"> 
                {fields}
            </div>
        )
    }

    renderForm() {
        var rows = []
        fields.forEach(row => {
            rows.push(this.renderRow(row));
        });
        return (
            <div>
                <form className="form validate-form" autoComplete="off">
                    {rows}
                    <RenderButton onSubmit={()=>{this.onSubmit()}}/>
                </form>
            </div>
        );
    }

    render() {
        return (
            /*
            <div>
                <form className="form validate-form" autoComplete="off">
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper validate-input" data-validate="Date is required">
                            <input className="input has-val" type="date" name="name" value={new Date().toDateInputValue()}/>
                            <span className="focus-input" data-placeholder="DATE"></span>
                        </div>
                        <div className="medium-wrapper validate-input" data-validate="ESP is required">
                            <input className="input" type="text" name="esp"/>
                            <span className="focus-input" data-placeholder="EST. DEPARTURE"></span>
                        </div>
                        <div className="medium-wrapper validate-input" data-validate="ESR is required">
                            <input className="input" type="text" name="esr"/>
                            <span className="focus-input" data-placeholder="EST. RETURN"></span>
                        </div>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper validate-input" data-validate="Captain is required">
                            <input className="input" type="text" name="captain"/>
                            <span className="focus-input" data-placeholder="CAPTAIN"></span>
                        </div>
                        <div className="medium-wrapper validate-input" data-validate="ESP is required">
                            <input className="input" type="text" name="esp"/>
                            <span className="focus-input" data-placeholder="FIRST OFFICER"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className="input" type="text" name="esr"/>
                            <span className="focus-input" data-placeholder="FLIGHT ENGINEER"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className="input" type="text" name="esr"/>
                            <span className="focus-input" data-placeholder="OTHER CREW"></span>
                        </div>
                    </div>
                    <div className="row-wrapper">

                        <div className="small-wrapper" data-validate="A/C is required">
                            <input className="input" type="text" name="A/C"/>
                            <span className="focus-input" data-placeholder="A/C"></span>
                        </div>
                        <div className="small-wrapper">
                            <input className="input" type="text" name="BACKUP"/>
                            <span className="focus-input" data-placeholder="BACKUP"></span>
                        </div>
                        <div className="medium-wrapper" data-validate="Mission is required">
                            <input className="input" type="text" name="MISSION"/>
                            <span className="focus-input" data-placeholder="MISSION"></span>
                        </div>
                        <div className="medium-wrapper" data-validate="Details is required">
                            <input className="input" type="text" name="DETAILS"/>
                            <span className="focus-input" data-placeholder="DETAILS"></span>
                        </div>
                        <div className="medium-wrapper" data-validate="Config is required">
                            <input className="input" type="text" name="CONFIG"/>
                            <span className="focus-input" data-placeholder="CONFIG"></span>
                        </div>
                    </div>
                    <div className="wrap-input validate-input" data-validate="Name is required">
                        <input className="input" type="text" name="remarks"/>
                        <span className="focus-input" data-placeholder="REMARKS"></span>
                    </div>


                    <div className="half-col-wrapper">
                        <div className="medium-wrapper" data-validate="Name is required">
                            <input className="input" type="text" name="name"/>
                            <span className="focus-input" data-placeholder="MEALS"></span>
                        </div>
                        <div className="medium-wrapper" data-validate="Name is required">
                            <input className="input" type="text" name="name"/>
                            <span className="focus-input" data-placeholder="DCO/DPCO/DNCO"></span>
                        </div>
                    </div>

                    <div className="container-form-btn">
                        <div className="wrap-form-btn">
                            <div className="form-bgbtn"></div>
                            <button className="form-btn">
                                Add Flight
                            </button>
                        </div>
                    </div>



                </form>

            </div>*/
            <div>
                {this.renderForm()}
            </div>


        );
    }
}

export default FlightForm;
