import React from 'react';
import logo, { ReactComponent } from './logo.svg';
import './App.css';

function NavBarIcon(props) {
  return (
      <div 
          className={props.open ? 'navBarIcon open': "navBarIcon"} 
          onClick={props.onClick} >
          <div className="bar1"></div>
          <div className="bar2"></div>
          <div className="bar3"></div>
      </div>
  )
}

function Header(props) {
  return <h1>{props.activeTab}</h1>
}

function NavBarTab(props) {
    return (
      <div className='navBarTab'
        onClick={props.onClick}>
        <a href="#">{props.tabName} </a>
      </div>
    )
}

class NavBar extends React.Component {


  renderNavBarIcon() {
    return <NavBarIcon open={this.state.open} onClick={()=> {this.toggleOpen()}}/>;
  }

  renderNavBarTab(tabName) {
    return <NavBarTab tabName={tabName} active={this.state.activeTab === tabName} onClick={() => {this.toggleTab(tabName)}}/>
  }

  shouldComponentUpdate(nextState) {
    return this.state.open !== nextState.open;
  }

  toggleOpen() {
    const currentState = this.state.open;
    this.setState({ open: !currentState });
  };

  toggleTab(tabName) {
    console.log(tabName);
  }

  render() {
    /** const tabs = '';
    this.state.navTabs.forEach(tab => {
      tabs = tabs.concat(this.renderNavBarTab(tab));
    });*/
    return (
      <div className={this.state.open ? 'openedNavBar navBar': "navBar"}>
        <div className="navBarIcon">
          {this.renderNavBarIcon()}
        </div>
        <div className={this.state.open ? 'openedNavBarTabs navBarTabs': 'navBarTabs'}>
          {this.renderNavBarTab('Dashboard')}
          {this.renderNavBarTab('Flights')}
        </div>
      </div>
    );
  }
}

class AppBody extends ReactComponent {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      activeTab: "Dashboard",
      navTabs: ["Dashboard", "Flights"],
    };
  }

  renderNavBar() {
    return <NavBar open={this.state.open} activeTab={this.state.activeTab} navTabs={this.state.navTabs}/>
  }

  renderHeader() {
    return <Header activeTab = {this.state.activeTab}/>
  }

  render() {
    return (
      <div className="App">
        <div className="header">
          {this.renderHeader()}
        </div>
        {this.renderNavBar()}     
      </div>
    );
  };
}

function App() {
  return (
    <AppBody/>
  );
}

export default App;
