import React from 'react';
import "./Flights.css";
import RIGHT_BTN from './img/add.png';
import LEFT_BTN from './img/add.png';
//import firebase from "./Firebase.js";

var today = new Date();
var dd = String(today.getDate()).padStart(2, '0');
var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
var yyyy = today.getFullYear();

today = mm + '/' + dd + '/' + yyyy;


// Add a new document in collection "cities"
/** firebase.collection("flights").add({
    name: "Los Angeles",
    country: "USA"
})
.then(function(docRef) {
    console.log("Document successfully written!", docRef.id);
})
.catch(function(error) {
    console.error("Error writing document: ", error);
});*/

function date(props) {
    var days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
    return (
        <div className='flight-date'>
            <h2>Thursday, May 2, 2019</h2>
        </div>
    )
}

function renderFlightDayHeader(props) {
    return (
        <div className="flight-day-header">
            <img className='date-btn' src={LEFT_BTN}></img>
            {date()}
            <img className='date-btn' src={RIGHT_BTN}></img>
        </div>
    )
}
function RenderFlightDay(props) {
    var headers = ['ETD', 'AC', 'CREW', 'MISSION', 'DETAILS', 'CONFIG', 'REMARKS', 'SQ PRI', 'DCO', 'ETR'];
    var tableHeaders = [];
    var dot = <span class="dot"></span>;
    headers.forEach(title => {
        tableHeaders.push(<th>{title}</th>)
    });
    var data = [['0900', '462', 'Amelia E | Charlies L', '427 AU H102 G00', 'SME', 'DC6S', "", '1 SOA', dot, '1030'], 
                ['2520', '462', 'D.B. Cooper| Maverick', '427 AU H105 G00', 'ALP1', 'DC6S', "", '3 SOA', dot, '1230']]
    var tableRows = [];
    data.forEach(row => {
        var r = [];
        for (var i = 0; i<row.length; i++) {
            r.push(<td className={headers[i].toLowerCase()}>{row[i]}</td>)
        };
        tableRows.push(<tr className={props.date.getHours() > parseInt(row[0][0]+row[0][1]) ? 'expired':''}>{r}</tr>);
    });
    return (
        <div className="flight-day">
            <table>
                <tr>
                    {tableHeaders}
                </tr>
                {tableRows}
            </table>
        </div>
    )
}

function RenderFlightDayBox(props) {
    return (
        <div className="flight-day-box">
            {renderFlightDayHeader()}
            <RenderFlightDay date={props.date}/>
        </div>
    )
}

function RenderInfoBox(props) {
    return (
        <div className="info-box">
            <div className="info-title">
                <h5>{props.title}</h5>
            </div>
            <div className="info-content" contentEditable="true">
                <h5>N/A</h5>
            </div>
        </div>
    )
}

function RenderInfoBar(props) {
    const default_info = ["EVENING TWILIGHT", "SNAGS DESK SGT", "DUTY MVP", "FDO", "DUTY GROUND RUN"];
    var boxes = [];
    for (var i = 0; i < default_info.length; i++) {
        var title = default_info[i]
        boxes.push(<RenderInfoBox title={title}/>)
    }
    return (
        <div className="info-bar">
            <div className="time-title">
                <h5>CURRENT TIME</h5>
            </div>
            <div className="time">
                <h2>{(props.date.getHours()<10?"0":"")+props.date.getHours()+':'+(props.date.getMinutes()<10?"0":"")+props.date.getMinutes()}</h2>
            </div>
            {boxes}
        </div>
    )
}

class Flights extends React.Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date()};
    }
    componentDidMount() {
        this.timerID = setInterval(() => this.tick(), 1000);
    }

    componentWillUnmount() {clearInterval(this.timerID);}

    tick() {this.setState({date: new Date()});}

    render() {
        return (
            <div>
                <RenderFlightDayBox date={this.state.date}/>
                <RenderInfoBar date={this.state.date}/>
            </div>
        )
    }
}

export default Flights;
