import React from 'react';
import './Dashboard.css';


function DayHeader(props) {
    return (

        <div className='dayHeader'>
            <h3 id='date'>{props.day + "  "}</h3><h3>{props.date}</h3>
        </div>
    )
}

function DayContents(props) {
    var content = [];
    props.events.forEach(event => {
        var [type, start, end, details] = event;
        content.push(
            <div className={type+'Event'} style={{height: ((end-start)*60)+'px'}}>
                <p>{(start < 10 ? '0' : '')+start*100 + " - " + (end < 10 ? '0' : '')+ end*100}</p>
                <h5 className='name'>{type[0].toUpperCase()+type.slice(1,)}</h5>
                <h6>{details}</h6>
            </div>
        )
    });
    return content;
}

function DayView(props) {
    return (
        <div className='dayView'>
            <DayHeader day={props.day} date={props.date}/>
            <DayContents events={props.events}/>
        </div>
    )
}

class WeekCalendar extends React.Component {

    constructor(props) {
        super(props);
    }

    render () {
        var days = ['Mon', 'Tues', 'Wed', 'Thur', 'Fri'];
        var content = [];
        for (var i=0; i < 5; i++) {
            content.push(<DayView day={days[i]} date={this.props.dates[i]} events={this.props.events[i]}/>)
        }
        return content;
    }
}

class MonthCalendar extends React.Component {
    constructor(props) {
        super(props);
    }

    render () {
        return (
            <div className="yellow">
                <div id="calendar">
                    <div id="toolbar"></div>
                    <div id="dates" className="show">
                        <div id="lastMt">&lsaquo;</div>
                        <div id="nextMt">&rsaquo;</div>
                        <div id="months-cont">
                            <div id="months">
                                <span className="active month">January</span><span className="month">February</span><span className="month">March</span><span className="month">April</span><span className="month">May</span><span className="month">June</span><span className="month">July</span><span className="month">August</span><span className="month">September</span><span className="month">October</span><span className="month">November</span><span className="month">December</span>
                            </div>
                        </div>
                    <div id="daysotweek">
                        <div className="day">S</div>
                        <div className="day">M</div>
                        <div className="day">T</div>
                        <div className="day">W</div>
                        <div className="day">T</div>
                        <div className="day">F</div>
                        <div className="day">S</div>
                    </div>
                    <div id="days"></div>
                    </div>
                </div>
            </div>)
    }
}

export default WeekCalendar;
export {MonthCalendar};
