import React from 'react';
import "./Flights.css";
import RIGHT_BTN from './img/right-arrow.png';
import LEFT_BTN from './img/left-arrow.png';
import firebase from "./Firebase.js";
import { ENGINE_METHOD_NONE } from 'constants';
import ErrorPage from './ErrorPage.js';
import ReactGA from 'react-ga';

var today = new Date();
var dd = String(today.getDate()).padStart(2, '0');
var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
var yyyy = today.getFullYear();

today = mm + '/' + dd + '/' + yyyy;

function initializeReactGA() {
    ReactGA.initialize('UA-108277898-5');
    ReactGA.pageview('/Flights');
}


function date(curDate) {
    //var today = new Date();
    var weekday = curDate.getDay();
    var day = curDate.getDate();
    var month = curDate.getMonth();
    var year = curDate.getFullYear();
    var days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    var months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November","December"];
    return (
        <div className='flight-date'>
            <h2>{days[weekday] +", "+months[month]+" "+day+", "+year}</h2>
        </div>
    )
}

function RenderFlightDayHeader(props) {
    var displayDate = new Date(props.date);
    var curDate = new Date();
    var diff = Math.round((displayDate.getTime() - curDate.getTime())/(24*60*60*1000));

    // Only show flights for last 3 days to the next 2 weeks
    var disableLeft = diff == -3;
    var disableRight= diff == 14;

    return (
        <div className="flight-day-header">
            <img className={'date-btn' + (disableLeft ? ' disabled': '')} src={LEFT_BTN} onClick={()=>{props.changeDate(disableLeft ? 0 : -1)}}></img>
            {date(displayDate)}
            <img className={'date-btn' + (disableRight ? ' disabled': '')} src={RIGHT_BTN} onClick={()=>{props.changeDate(disableRight ? 0 : 1)}}></img>
        </div>
    )
}

function parseName(name) {
    return name[1].toUpperCase() + name.slice(2);
}


function RenderFlightDay(props) {

    var headers = ['ETD', 'AC', 'CREW', 'MISSION', 'DETAILS', 'CONFIG', 'REMARKS', 'SQ PRI', 'DCO', 'ETR'];
    var tableHeaders = [];
    headers.forEach(title => {
        tableHeaders.push(<th>{title}</th>)
    });
    var tableRows = [];


    if (props.data != null) {props.data.forEach(entry => {
        var d = entry.data();
        var r = [<td className='etd'>{(d.timeStart < 100 ? '0' : '') +(d.timeStart < 1000 ? '0' : '') + d.timeStart}</td>,
        <td className='ac'><div className='ac-wrap'>{d.ac}</div></td>,
        <td className='crew'>{parseName(d.captain) + ' | ' + parseName(d.fo)}</td>,
        <td className='mission'>{d.mission}</td>,
        <td className='details'>{d.details}</td>,
        <td className='config'>{d.config}</td>,
        <td className='remarks'>{d.remarks}</td>,
        <td className='pri'>{d.pri + ' SOA'}</td>,
        <td className='dco'><div className={"dot "+d.dco}></div></td>,
        <td className='etr'>{(d.timeEnd < 100 ? '0' : '') +(d.timeEnd < 1000 ? '0' : '') + d.timeEnd}</td>,
        ];

        var startTime = parseInt(d.timeStart);
        tableRows.push(<tr className={(props.curTime.getDate() == props.date.getDate() && startTime < props.curTime.getHours()*100+props.curTime.getMinutes()) ? 'expired':''} onClick={props.toggleOverlay.bind(this,entry.id)}>{r}</tr>);
        })

        return (
            <div className="flight-day">
                <table>
                    <tr>
                        {tableHeaders}
                    </tr>
                    {tableRows}
                </table>
            </div>
        )
    }
    else {
        return (
            <div className="addFlight" onClick={props.changeTab}>
                <img className="addFlightLogo" src={HELI_GRAY}/>
                <div className="addFlightText"> 
                    <h4> Add a Flight </h4>
                </div>
            </div>
        )
    }

}

function RenderFlightDayBox(props) {
    return (
        <div className="flight-day-box">
            <RenderFlightDayHeader date={props.date} changeDate={(offset)=>{props.changeDate(offset)}}/>
            <RenderFlightDay date={props.date} data={props.data} curTime={props.curTime} toggleOverlay={(id)=>{props.toggleOverlay(id)}}/>
        </div>
    )
}

function RenderInfoBox(props) {
    return (
        <div className="info-box">
            <div className="info-title">
                <h5>{props.title}</h5>
            </div>
            <div className="info-content" contentEditable={true} suppressContentEditableWarning={true} onBlur={(event)=>{props.updateDayStats(props.id, event.target.textContent);}}>
                <h5>{props.value[props.id]}</h5>
            </div>
        </div>
    )
}

function RenderInfoBar(props) {
    const default_info = {"EVENING TWILIGHT":props.twilight, "SNAGS DESK SGT": props.sgt, "DUTY MVP": props.mvp, "FDO": props.fdo, "DUTY GROUND RUN": props.groundrun};
    var boxes = [];
    boxes.push(<RenderInfoBox title={"EVENING TWILIGHT"} value={props} id={'twilight'} updateDayStats={(f, e)=>{props.updateDayStats(f,e)}}/>)
    boxes.push(<RenderInfoBox title={"SNAGS DESK SGT"} value={props} id={'sgt'} updateDayStats={(f, e)=>{props.updateDayStats(f,e)}}/>)
    boxes.push(<RenderInfoBox title={"DUTY MVP"} value={props} id={'mvp'} updateDayStats={(f, e)=>{props.updateDayStats(f,e)}}/>)
    boxes.push(<RenderInfoBox title={"FDO"} value={props} id={'fdo'} updateDayStats={(f, e)=>{props.updateDayStats(f,e)}}/>)
    boxes.push(<RenderInfoBox title={"DUTY GROUND RUN"} value={props} id={'groundrun'} updateDayStats={(f, e)=>{props.updateDayStats(f,e)}}/>)

    return (
        <div className="info-bar">
            <div className="time-title">
                <h5>CURRENT TIME</h5>
            </div>
            <div className="time">
                <h2>{(props.curTime.getHours()<10?"0":"")+props.curTime.getHours()+':'+(props.curTime.getMinutes()<10?"0":"")+props.curTime.getMinutes()}</h2>
            </div>
            {boxes}
        </div>
    )
}

class Flights extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            curTime: new Date(),
            date: new Date(),
            data: null,
            twlight: "N/A",
            sgt: "N/A", 
            mvp: "N/A", 
            fdo: "N/A",
            groundrun: "N/A",
            hasError: false,
        }
    }
    componentDidMount() {
        this.timerID = setInterval(() => this.tick(), 1000);
