import React from 'react';
import "./Overlay.css";

import FlightForm from "./FlightForm.js";
import firebase from './Firebase.js';
import EDIT from './img/edit.png';
import DELETE from './img/delete.png';
import {deleteFlight} from "./FirebaseHelper.js";
import ErrorPage from './ErrorPage';


class Overlay extends React.Component {

    constructor(props) {
        super(props);
        this.state = {data: {},
                    captain: "", 
                    fo: "", 
                    fe: "", 
                    crew: "",
                    hasError: false,
                }
    }

    deleteClicked(id, data, onClose) {
        if (window.confirm("Are you sure you want to delete this event? \nIt will be deleted for all participants")) {
            console.log("Deleting event... this isn't built yet tho. :(");
            deleteFlight(id, data);
            onClose();
        } 
    }

    getPilots(type, id) {
        if (id) {
            firebase.firestore().collection("pilots").doc(id)
            .get()
            .then((querySnapshot) => {
                switch(type) {
                    case 'fo': 
                        this.setState({fo: querySnapshot.data().displayName})
                        return;
                    case 'fe': 
                        this.setState({fe: querySnapshot.data().displayName})
                        return;
                    case 'crwe': 
                        this.setState({crew: querySnapshot.data().displayName})
                        return;
                    default: 
                        this.setState({captain: querySnapshot.data().displayName})
                        return;
                }

            })
            .catch(function(error) {
                console.log(error);
            });
        }
    }

    Sidebar() {
        if (this.state.data) {
            //TODO: fix this not sure why it is offset by -1
            var d = new Date(this.state.data.date);
            d.setDate(d.getDate() + 1);

            var [timeStart, timeEnd] = [this.state.data.timeStart, this.state.data.timeEnd];
            var time = (timeStart < 100 ? '0' : '') + (timeStart < 1000 ? '0' : '') + timeStart + ' - ' + (timeEnd < 100 ? '0' : '') +(timeEnd < 1000 ? '0' : '') + timeEnd;
            return (
                <div className={'sidebar '+'flight-event'}>
                    <h4>{d.toDateString().replace(' ', ', ')}</h4>
                    <div className='sidebar-content'>
                        <h4>{time}</h4>
                        <h1>{this.state.data.details}</h1>
                    </div>
                </div>
        )}
        return
    }
    ModalContent() {
        var content = "";

        if (this.state.data == null) { 
            throw new Error("This event doesn't exist!");
        }
        if (Object.keys(this.state.data).length > 0) {
            var d = this.state.data;
            if (this.props.event == 'meeting') {
                content = (
                <tbody>
                        <tr><td className='bold'>TITLE</td><td>{d.title}</td></tr>
                        <tr><td className='bold'>MEMBER</td><td>{d.userid[0].toUpperCase() + ". " + d.userid[1].toUpperCase() + d.userid.slice(2)}</td></tr>
                        <tr><td className='bold'>CONFERENCE LINK</td><td>{d.confLink || ''}</td></tr>
                        <tr><td className='bold'>LOCATION</td><td>{d.location || ''}</td></tr>
                        <tr><td className='bold'>NOTES</td><td>{d.notes}</td></tr>
                    </tbody>
                )
            }
            if (this.props.event == 'flight') {
                content = (
                <table>
                    <tbody>
                        <tr><td className='bold'>CAPTAIN</td><td>{this.state.captain}</td></tr>
                        <tr><td className='bold'>FIRST OFFICER</td><td>{this.state.fo}</td></tr>
                        {d.fe ? <tr><td className='bold'>FLIGHT ENG</td><td>{this.state.fe}</td></tr> : ''}
                        {d.crew ? <tr><td className='bold'>CREW</td><td>{this.state.crew}</td></tr> : ''}
                        <tr><td className='bold'>AC</td><td>{d.ac}</td></tr>
                        {d.backup ? <tr><td className='bold'>BACKUP</td><td>{d.backup}</td></tr> : ''}
                        <tr><td className='bold'>DETAILS</td><td>{d.details || ''}</td></tr>
                        <tr><td className='bold'>CONFIG</td><td>{d.config || ''}</td></tr>
                        {d.remarks ? <tr><td className='bold'>REMARKS</td><td>{d.remarks}</td></tr> : ''}
                        <tr><td className='bold'>SQN PRI</td><td>{d.pri || ''}</td></tr>
                        <tr><td className='bold'>MEAL</td><td>{d.meal ? 'Provided' : 'Not Provided'}</td></tr> 
                        <tr><td className='bold'>DCO</td><td><div className={"dot "+d.dco}></div></td></tr>
                    </tbody>
                </table>)
            };
        }
        return (
            <div className={'modal-content'}>
                <div className='buttons'>
                    <img className="delete" src={DELETE} title='Delete' onClick={()=>{this.deleteClicked(this.props.id, this.state.data, this.props.onClose)}} />
                    <img className="edit" src={EDIT} title='Edit'/>
                    <span className="close" title='Close Dialog' onClick={this.props.onClose}>&times;</span>
                </div>
                {content}
            </div>
        )
    }

    componentDidMount() {
        var id = this.props.id || 'IYVPMzWSTltD3N6k9tny';
        firebase.firestore().collection("events").doc(id)
        .get()
        .then((querySnapshot) => {
            var d = querySnapshot.data();
            console.log(d);
            this.setState({data: d});
            this.getPilots('captain', d.captain);
            this.getPilots('fo', d.fo);
            this.getPilots('fe', d.fe);
            this.getPilots('crew', d.crew);
        })
        .catch(function(error) {
            console.log("Error getting documents: ", error);
        });
    }

    componentDidCatch(error, info) {
        // Display fallback UI
        this.setState({ hasError: true });
    }

    render() {
        if (this.state.hasError) {
            return <ErrorPage/>
        }
        /*return (
            <div className="overlay" onClick={this.props.onClose}>
                <div className="overlay-content">
                    <span className="close" onClick={this.props.onClose}>&times;</span>
                    <div className="overlay-title"><h1>Flight Details</h1></div>
                    <FlightForm></FlightForm>
                </div>
            </div>
        )*/
        return (
            <div className="overlay" onClick={this.props.onClose}>
                <div className="overlay-content" onClick={(e)=>{e.stopPropagation()}}>
                    {this.Sidebar()}
                    {this.ModalContent()}
                </div>
            </div>
        )
    }
}

export default Overlay;
