import React from 'react'
import "./Login.css"
import firebase from "./Firebase.js";


function checkInput(input) {
    if (input == "Amelia Earhart") {
        document.getElementsByClassName("pswdForm")[0].hidden = false;
        document.getElementsByClassName("pswdInput")[0].hidden = false;
    }
    else {
        if (document.getElementsByClassName("pswdForm")[0].hidden == false) {
            document.getElementsByClassName("pswdForm")[0].hidden = true;
            document.getElementsByClassName("pswdInput")[0].hidden = true;
        }
    }
}

function LoginForm() {
    var pilots = []
    console.log(firebase.collection('pilots').get())

    return (
        <form className="form">
            <h2 className="userForm">Please enter your name</h2>
            <input className="userInput" type="text" list="userList" onChange={(e)=>{checkInput(e.target.value.trim())}}/>
            <datalist id="userList">
                <option value="Amelia Earhart"/>
                <option value="D.B. Cooper"/>
                <option value="Charles Lindbergh"/>
            </datalist>
            <br/>
            <h2 className="pswdForm" hidden="true">Please enter your password</h2>
            <input className="pswdInput" type="password" hidden="true"/>
            <RenderButton/>
        </form>
    )
}

function RenderButton(props) {
    return (
        <div className="container-form-btn">
                <button className="form-btn" onClick={props.onSubmit}>
                    Next
                </button>
        </div>
    );
}

class Login extends React.Component{
    constructor(props) {
        super(props)
    }
    render() {
        return (
            <LoginForm/>
        )
    }
}

export default Login;
