import React from "react";
import "./Form.css";
import firebase from "./Firebase.js";
import {firebaseCreateDate} from "./FirebaseHelper.js";
import {RenderSuccessDialog} from "./Overlay.js";

Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});

const fields = [[{name: "date", custom: true, size: 'medium', required: true}, 
{name: "timeStart", custom: true, displayName: "est. departure", size: 'medium', required: true}, 
{name: "timeEnd", custom: true, displayName: "est. return", size: 'medium', required: true}, 
{name: "flighttime", custom: true}],

[{name: "captain", select: 'pilots', size: 'medium', required: false}, 
{name: "fo", select: 'pilots', displayName: "first officer", size: 'medium', required: false}, 
{name: "fe", select: 'pilots', displayName: "flight engineer", size: 'medium', required: false}, 
{name: "crew", select: 'pilots', displayName: "other crew", size: 'medium', required: false}], 

[{name: "ac", size: 'small', required: false}, 
{name: "backup", size: 'small', required: false}, 
{name: "mission", size: 'medium', required: false}, 
{name: "details", size: 'medium', required: false}, 
{name: "config", size: 'medium', required: false}], 
[{name: "remarks", size: 'large', required: false}], 
[{name: "pri", displayName: 'sqn pri', size: 'medium', required: false}, 
{name: "meal", size: 'medium', required: false},
{name: "dco", select: 'dco', size: 'medium', required: false}] ];

function RenderButton(props) {
    return (
        <div className="container-form-btn">
            <div className="wrap-form-btn">
                <div className="form-bgbtn"></div>
                <button className="form-btn" onClick={props.onSubmit}>
                    Add Flight
                </button>
            </div>
        </div>
    );
}



class FlightForm extends React.Component {
    constructor(props) { 
        super(props);
        this.state = {
            date: new Date().toDateInputValue(),
            timeStart: '0000',
            timeEnd: '0000',
            captain: "",
            fo: "",
            fe: "",
            crew: "",
            ac: "",
            backup: "",
            mission: "",
            details: "",
            config: "",
            remarks: "",
            pri: "",
            meal: "",
            dco: "",
            type: "flight",
        }
    }

    firebaseSetBool(pilot) {
        var flightDate = 'flights.' + this.state.date;
        firebase.firestore().collection("pilots").doc(pilot).update({[flightDate]: true})
        .then(function() {
            console.log("Boolean successfully set for pilot");})
        .catch(function(error) {
            console.error("Error setting flight boolean", error);
        })
    }

    onSubmit() {
        var ok = true;
        console.log(this.state);
        var newEvent = firebase.firestore().collection("events").doc();
        newEvent.set(this.state)
        .then(function() {
            console.log("Event successfully added to events!");})
        .catch(function(error) {
            console.error("Error adding event: ", error);
            ok = false;
        });

        this.firebaseSetBool(this.state.captain);
        this.firebaseSetBool(this.state.fo);
        var updates = {};
        updates['events.'+this.state.captain] = firebase.firestore.FieldValue.arrayUnion({'type':'flight', 'startTime': this.state.timeStart, 'endTime': this.state.timeEnd, 'id':newEvent.id});
        updates['events.'+this.state.fo] = firebase.firestore.FieldValue.arrayUnion({'type':'flight', 'startTime': this.state.timeStart, 'endTime': this.state.timeEnd, 'id':newEvent.id});
        var newUpcomingEvents = [   firebase.firestore().collection('pilots').doc(this.state.captain).collection('upcoming-events').doc(),
                                    firebase.firestore().collection('pilots').doc(this.state.fo).collection('upcoming-events').doc()];

        if (this.state.fe) {
            updates['events.'+this.state.fe] = firebase.firestore.FieldValue.arrayUnion({'type':'flight', 'startTime': this.state.timeStart, 'endTime': this.state.timeEnd, 'id':newEvent.id});
            newUpcomingEvents.push(firebase.firestore().collection('pilots').doc(this.state.fe).collection('upcoming-events').doc());
            this.firebaseSetBool(this.state.fe);
        }
        if (this.state.crew) {
            updates['events.'+this.state.crew] = firebase.firestore.FieldValue.arrayUnion({'type':'flight', 'startTime': this.state.timeStart, 'endTime': this.state.timeEnd, 'id':newEvent.id});
            newUpcomingEvents.push(firebase.firestore().collection('pilots').doc(this.state.crew).collection('upcoming-events').doc());
            this.firebaseSetBool(this.state.crew);
        }
        let date = this.state.date;
        firebase.firestore().collection("dates").doc(date).update(updates)
        .then(function() {
            console.log("Event successfully added to dates!");})
        .catch(function(error) {
            firebaseCreateDate(date);
        })


        newUpcomingEvents.forEach((x)=>{x.set({
            ac: this.state.ac,
            captain: this.state.captain,
            flightid: newEvent.id,
            config: this.state.config,
            date: this.state.date,
            fo: this.state.fo,
            fe: this.state.fe,
            crew: this.state.crew,
            details: this.state.details,
            timeEnd: this.state.timeEnd,
            timeStart: this.state.timeStart,})
        .then(function() {
            console.log("Event successfully added to pilots!");})
        .catch(function(error) {
            console.error("Error writing document: ", error);
            ok = false;
        })})

        if (ok) {
            //window.location.reload();
            document.getElementsByClassName("success-modal")[0].style.display = "block";
        }

    }

    renderField(field) {
        //TODO: fix datalist to be dynamic
        const name = field.name;
        const displayName = field.hasOwnProperty("displayName") ? field.displayName.toUpperCase() : field.name.toUpperCase();
        const wrapperClassName = field.size + "-wrapper";
        const fieldClassName = "input"+ (this.state[name] != ""? " has-val": "");
        const datalist = field.hasOwnProperty('select') ? field.select : '';
        return (
            <div className={wrapperClassName + " validate-input"} data-validate = "Message is required">
            <datalist id="pilots">
                <option value="aearhart"/>
                <option value="cyeager"/>
                <option value="clindbergh"/>
                <option value="ehartmann"/>
                <option value="jdoolittle"/>
                <option value="owright"/>
                <option value="pmitchell"/>
                <option value="wwright"/>
            </datalist>
            <datalist id="dco">
                <option value="ok"/>
                <option value="warn"/>
                <option value="bad"/>
            </datalist>
            <input className={fieldClassName} autoComplete="new-password"  required={field.required} type="text" name={name} list={datalist} onBlur={(e)=>{this.setState({[name]: e.target.value.trim()});}}/>
            <span className="focus-input" data-placeholder={displayName}></span>
            </div>

        )
    }

    renderCustomField(field) {
        switch(field.name) {
            case "flighttime": 
                var time = "0 HRS 0 MIN";
                var [start, end] = [this.state.timeStart.toString(), this.state.timeEnd.toString()];
                if (this.state.timeStart < 1000) {
                    start = '0' + start;
                }
                if (this.state.timeEnd < 1000) {
                    end = '0' + end;
                }
                var hours = parseInt(end.substring(0, 2))- parseInt(start.substring(0, 2));
                var minutes = parseInt(end.substring(2, 4))- parseInt(start.substring(2, 4));

                if (hours < 0) {
                    hours += 24
                }
                if (minutes < 0) {
                    minutes += 60;
                    hours -= 1;
                }
                if (minutes >= 60) {
                    minutes -= 60;
                    hours += 1;
                }
                if (hours || minutes){ 
                    time = hours + " HRS " + minutes + " MIN";
                }
                return (<div>
                    <input className='input has-val' type="text" disabled={true} value={time}/>
                </div>);
            case "pri": 
                /*return (<div className="small-wrapper">
                    <input className='input' required={true} type="text"/> 
                    <span className="focus-input" data-placeholder="SQN PRI"></span>
                </div>);*/
            case "date": 
                return (<div className="medium-wrapper">
                <input className="input has-val" type="date" name="name" defaultValue={new Date().toDateInputValue()} onBlur={(e)=>{var d = new Date(e.target.value);
                    d.setMinutes( d.getMinutes() + d.getTimezoneOffset() );
                    this.setState({date: d.toDateInputValue()});}}/>
                <span className="focus-input" data-placeholder="DATE"></span>
            </div>)
            default: 
                const name = field.name;
                const displayName = field.hasOwnProperty("displayName") ? field.displayName.toUpperCase() : field.name.toUpperCase();
                const fieldClassName = "input"+ (this.state[name] != ""? " has-val": "")
                return (<div className={field.size + "-wrapper"}>
                    <input className={fieldClassName} autoComplete="new-password" required={field.required} type="number" name={name} onBlur={(e)=>{this.setState({[name]: e.target.value.trim()});}}/>
                    <span className="focus-input" data-placeholder={displayName}></span>
                </div>);
        }
    }

    renderRow(row) {
        var fields = [];
        row.forEach(field => {
            if (field.hasOwnProperty('custom')) {
                fields.push(this.renderCustomField(field));
            }
            else {
                fields.push(this.renderField(field));
            }

        });
        return (
            <div className="row-wrapper"> 
                {fields}
            </div>
        )
    }

    renderForm() {
        var rows = []
        fields.forEach(row => {
            rows.push(this.renderRow(row));
        });
        return (
            <div>
                <form className="form" autoComplete="new-password"  onSubmit={(e)=>{e.preventDefault(); this.onSubmit()}}>
                    {rows}
                    <RenderSuccessDialog/>
                    <RenderButton/>
                </form>
            </div>
        );
    }

