import React from "react";
import "./Form.css";
import firebase from "./Firebase.js";
import Helper from "./Helper.js";
import {RenderSuccessDialog} from "./Overlay.js";

Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});

class MeetingForm extends React.Component {
    constructor(props) { 
        super(props);
        //allPilotIDs
        this.state = {
            title: "", 
            type: "meeting", 
            date: new Date().toDateInputValue(),
            startTime: 0,
            endTime: 0,
            location: "", 
            confLink: "",
            notes: "",
            userid: "",
            p1:"",p2:"",p3:"",p4:"",
            ok: true,
        }
    }

    checkError(field, value) {
        switch (field){
        case 'p1':
            if (!this.props.allPilotIDs.includes(value) && value != "") {
                Helper.flag('p1', 'Invalid pilot');
                this.setState({ok: false});
            }
            else {this.setState({ok: true});}
            break;
        case 'p2':
            if (!this.props.allPilotIDs.includes(value) && value != "") {
                Helper.flag('p2', 'Invalid pilot');
                this.setState({ok: false});
            }
            else {this.setState({ok: true});}
            break;
        case 'p3':
            if (!this.props.allPilotIDs.includes(value) && value != "") {
                Helper.flag('p3', 'Invalid pilot');
                this.setState({ok: false});
            }
            else {this.setState({ok: true});}
            break;
        case 'p4':
            if (!this.props.allPilotIDs.includes(value) && value != "") {
                Helper.flag('p4', 'Invalid pilot');
                this.setState({ok: false});
            }
            else {this.setState({ok: true});}
            break;
        case 'startTime':
            if (isNaN(value) || !Helper.timeValid(value)) {
                Helper.flag('startTime', 'Enter between 0000 and 2359');
                this.setState({ok: false});
            }
            else if (parseInt(value) >= parseInt(this.state.endTime)) {
                Helper.flag('endTime', 'Return must be after dep.');
                this.setState({ok: false});
            }
            else {this.setState({ok: true});}
            break;
        case 'endTime':
            if (isNaN(value) || !Helper.timeValid(value)) {
                Helper.flag('endTime', 'Enter between 0000 and 2359');
            }
            else if (parseInt(this.state.startTime) >= parseInt(value)) {
                Helper.flag('endTime', 'Return must be after dep.');
                this.setState({ok: false});
                }
                else {this.setState({ok: true});}
                break;

        default: 
            this.setState({ok: true});
            break;
        }
    }

    onSubmit() {
        var required = ['title', 'startTime', 'endTime'];
        var remove = document.getElementsByClassName('alert-validate');
        for (let item of remove) {
            item.classList.remove('alert-validate');
        }
        required.forEach(field => {
            if (this.state[field] == "") {
                Helper.flag(field, "Required");
                this.setState({ok: false});
            }
        })
        var fieldsToCheck =['startTime', 'endTime','p1','p2','p3','p4'];
        fieldsToCheck.forEach((field) => {
            if (this.state.ok) {
                this.checkError(field, this.state[field])
            }
        });


        if (this.state.ok) {
            //TODO TODO TODO : now we have 4 users potentially!!! How to deal with this? 
            //TODO: UPDATE OVERLAY TO DISPLAY THIS 
            var newEvent = firebase.firestore().collection("events").doc()
            newEvent.set(this.state)
            .then(function() {
                console.log("Event successfully added to events!");})
            .catch(function(error) {
                console.error("Error adding event: ", error);
            });

            var users = [this.state.userid, this.state.p1, this.state.p2, this.state.p3, this.state.p4];
            users.forEach((user) => {
                var field = 'events.'+user;
                firebase.firestore().collection("dates").doc(this.state.date).update({
                    [field]: firebase.firestore.FieldValue.arrayUnion({'type':'meeting', 'startTime': this.state.startTime, 'endTime': this.state.endTime, 'id':newEvent.id})
                    })
                .then(function() {
                    console.log("Event successfully added to dates!");})
                .catch(function(error) {
                    console.error("Error writing document: ", error);
                })
            })

            document.getElementsByClassName("success-modal")[0].style.display = "block";
        }
    }

    onBlur(name, e, type) {
        if (type == 'date') {
            var d = new Date(e.target.value);
            d.setMinutes( d.getMinutes() + d.getTimezoneOffset() );
            this.setState({date: d.toDateInputValue()});
        }
        else if (type == 'number')  {
            this.setState({[name]: e.target.value});
        }
        else {
            this.setState({[name]: e.target.value.trim()});
        }
        e.target.parentElement.classList.remove("alert-validate");
    }

    componentDidMount() {
        this.setState({userid: (this.props.activeUser[0] + this.props.activeUser.split(' ')[1]).toLowerCase()});
    }


    render() {

        return (
            <div>
                <RenderSuccessDialog/>
                <form className="form validate-form" autoComplete="off" >
                    <div className="wrap-input validate-input" id="title">
                        <input className={"input"+ (this.state.title !== ""? " has-val": "")} type="text" name="title" required={true} onBlur={(e)=>{this.onBlur('title',e)}}/>
                        <span className="focus-input" data-placeholder="MEETING TITLE"></span>
                    </div>
                    <div className="row-wrapper"> 
                    <div className="medium-wrapper">
                            <input className={"input has-val"}  required={true}type="date" name="date" defaultValue={new Date().toDateInputValue()} onBlur={(e)=>{this.onBlur('date',e, 'date')}}/>
                            <span className="focus-input" data-placeholder="DATE"></span>
                        </div>
                        <div className="medium-wrapper validate-input" id="startTime">
                            <input className={"input"+ (this.state.startTime !== 0 ? " has-val": "")} type="text" name="startTime" 
                                required={true}
                                onBlur={(e)=>{this.onBlur('startTime',e, 'number')}}/>
                            <span className="focus-input" data-placeholder="START TIME"></span>
                        </div>
                        <div className="medium-wrapper validate-input" id="endTime">
                            <input className={"input"+ (this.state.endTime !== 0 ? " has-val": "")} type="text" name="endTime" 
                                required={true}
                                onBlur={(e)=>{this.onBlur('endTime',e, 'number')}}/>
                            <span className="focus-input" data-placeholder="END TIME"></span>
                        </div>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper validate-input" id="p1">
                        {Helper.getOptionsList(this.props.allPilotIDs)}
                            <input className={"input"+ (this.state.p1 !== "" ? " has-val": "")}name="p1" list="pilots" type="text" onBlur={(e)=>{this.setState({p1: e.target.value.trim()}); this.checkError(e.target.name, e.target.value.trim());}}/>
                            <span className="focus-input" data-placeholder="ATTENDEE 1"></span>
                        </div>
                        <div className="medium-wrapper validate-input" id="p2" >
                            <input className={"input"+ (this.state.p2 !== "" ? " has-val": "")} name="p2" list="pilots" type="text" onBlur={(e)=>{this.setState({p2: e.target.value.trim()}); this.checkError(e.target.name, e.target.value.trim());}}/>
                            <span className="focus-input" data-placeholder="ATTENDEE 2"></span>
                        </div>
                        <div className="medium-wrapper validate-input" id="p3" >
                            <input className={"input"+ (this.state.p3 !== "" ? " has-val": "")} name="p3" list="pilots" type="text" onBlur={(e)=>{this.setState({p3: e.target.value.trim()}); this.checkError(e.target.name, e.target.value.trim());}}/>
                            <span className="focus-input" data-placeholder="ATTENDEE 3"></span>
                        </div>
                        <div className="medium-wrapper validate-input" id="p4" >
                            <input className={"input"+ (this.state.p4 !== "" ? " has-val": "")} name="p4" list="pilots" type="text" onBlur={(e)=>{this.setState({p4: e.target.value.trim()}); this.checkError(e.target.name, e.target.value.trim());}}/>
                            <span className="focus-input" data-placeholder="ATTENDEE 4"></span>
                        </div>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper">
                            <input className={"input"+ (this.state.location !== "" ? " has-val": "")} type="text" name="esp" onBlur={(e)=>{this.onBlur('location',e)}}/>
                            <span className="focus-input" data-placeholder="LOCATION"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className={"input"+ (this.state.confLink !== "" ? " has-val": "")} type="text" onBlur={(e)=>{this.onBlur('confLink',e)}}/>
                            <span className="focus-input" data-placeholder="CONFERENCE LINK"></span>
                        </div>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="large-wrapper">
                            <input className={"input"+ (this.state.notes !== "" ? " has-val": "")} type="text" onBlur={(e)=>{this.onBlur('notes',e)}}/>
                            <span className="focus-input" data-placeholder="NOTES"></span>
                        </div>
                    </div>

                    <div className="container-form-btn">
                        <div className="wrap-form-btn">
                            <div className="form-bgbtn"></div>
                            <button className="form-btn" onClick={(e)=>{e.preventDefault();this.onSubmit()}}>
                                Add Meeting
                            </button>
                        </div>
                    </div>
                </form>

            </div>

        );
    }
}

export default MeetingForm;
