import React from 'react';
import './index.css';
import './Dashboard.css';
import moment from 'moment';
import ADD_LOGO from './img/add.png';
import WeekCalendar, { MonthCalendar } from './Calendar.js';

function FlightCard(props) {
    return (
        <div className="flightCard" onClick={props.toggleOverlay}>
            <div className="date">
                <h2>{props.flight}</h2>
                <p>9:00 - 12:00</p>
            </div>
            <hr/>
            <div className="infoLine">
                <div className="title">
                    <h5>CAPT</h5>
                </div>
                <div className="text">
                    <h5>Amelia Earheart</h5>
                </div>
            </div>
            <div className="infoLine">
                <div className="title">
                    <h5>FO</h5>
                </div>
                <div className="text">
                    <h5>Charles Hindbergh</h5>
                    <h5>D.B. Cooper</h5>
                </div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>A/C</h5></div>
                <div className="text"><h5>483</h5></div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>TYPE</h5></div>
                <div className="text"><h5>ALP 1</h5></div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>CONFIG</h5></div>
                <div className="text"><h5>DC6S</h5></div>
            </div>
        </div>
    );
}

function WeekView (props) {
    return (
        <WeekCalendar events={props.events} dates={props.curWeek}/>
    )
}

function MonthView (props) {
    return (
        <MonthCalendar curWeek={props.curWeek} changeCurWeek={()=>{props.changeCurWeek}}/>
    )
}

class Dashboard extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            curDate: new Date(),
            curWeek: this.initWeek(),
        }
    }

    initWeek() {
        var week = [];
        var cur = new Date();
        var startOfWeek = moment(cur).startOf('isoweek');
        for (var i =0; i < 5; i++) {
            week.push([startOfWeek.month(), startOfWeek.date()]);
            startOfWeek.add(1, 'day');
        }
        return week; 
    }

    changeCurWeek(day) {
        var week = [];
        var startOfWeek = day.startOf('isoweek');
        for (var i =0; i < 5; i++) {
            week.push([startOfWeek.month(), startOfWeek.date()]);
            startOfWeek.add(1, 'day');
        }

        this.setState({curWeek: week});

    }



    renderUpcomingEvents() {
        var events = [[['flight', 12, 13, 'ALP'],['apt', 13, 14, 'Medical'], ['apt', 15, 17]], 
        [['meeting', 10, 11, 'Classroom 2'], ['apt', 13, 16]],
        [['flight', 8, 9], ['meeting', 12, 15]],
        [['meeting', 6, 8], ['meeting', 14, 15, 'Classroom A']],
        [['flight', 3, 5, 'SME'], ['apt', 5, 6]]];
        var dates = [4, 5, 6, 7, 8];


        return (
            <div className="upcomingEvents">
                <div className="weekView">
                    <WeekView events={events} curWeek={this.state.curWeek}/>
                </div>
                <div className="monthView">
                    <MonthView events={events} curWeek={this.state.curWeek} changeCurWeek={(day)=>{this.changeCurWeek(day)}}/>
                </div>
            </div>
        )
    }

    renderUpcomingFlights() {
        this.initWeek();
        var cards = [];
        this.props.flights.forEach(flight => {
            cards.push(<FlightCard flight={flight} toggleOverlay={()=>{this.props.toggleOverlay('AC250', '7893521')}}/>)
        });
        return cards
    }
    render() { 
        return (
            <div>
                <h2>My Upcoming Flights</h2>
                <div className="upcomingFlights">
                    {this.renderUpcomingFlights()}
                </div>
                <h2>My Schedule</h2>
                {this.renderUpcomingEvents()}
            </div>

        )
    };
}

export default Dashboard;
