import React from 'react';
import "./Overlay.css";

import FlightForm from "./FlightForm.js";
import firebase from './Firebase.js';
import EDIT from './img/edit.png';
import EDIT_HOVER from './img/edit-hover.png';

function getPilots(pilots, cap, fo, fe, crew)  {
    var ids = [cap, fo, fe, crew];
    for (var i = 0; i < 4; i++) {
        if (ids[i]) {
            firebase.firestore().collection("pilots").doc(ids[i])
            .get()
            .then((querySnapshot) => {
                switch(i) {
                    case 0: 
                    console.log(querySnapshot.data())
                        pilots.captain = querySnapshot.data().displayName;
                        break;
                    case 1: 
                        pilots.fo = querySnapshot.data().displayName;
                        break;
                    case 2: 
                        pilots.fe = querySnapshot.data().displayName;
                        break;
                    default: 
                        pilots.crew = querySnapshot.data().displayName;
                        break;
                }
                console.log(pilots);
            })
            .catch(function(error) {
                console.log(error);
            });
        }
    };
    if (cap) {
        firebase.firestore().collection("pilots").doc(cap)
        .get()
        .then((querySnapshot) => {
            pilots.captain = querySnapshot.data().displayName;
        })
        .catch(function(error) {
            console.log(error);
        });
    }
}

class Overlay extends React.Component {

    constructor(props) {
        super(props);
        this.state = {data: {}}
    }


    Sidebar() {
        var content = ""
        return (
            <div className={'sidebar '+'flight-event'}>
                <h4>Thurs, May 2, 2019</h4>
                <div className='sidebar-content'>
                    <h4>1000 - 1300</h4>
                    <h1>SME</h1>
                </div>
            </div>
        )
    }
    ModalContent() {
        var content = "";


        if (Object.keys(this.state.data).length > 0) {
            var d = this.state.data;
            var pilots = {captain: "", fo: "", fe: "", crew: ""};
            getPilots(pilots, d.captain, d.fo, d.fe, d.crew);
            console.log(pilots)
            content = (
            <table>
                <tbody>
                    <tr><td className='bold'>CAPTAIN</td><td>{pilots.captain}</td></tr>
                    <tr><td className='bold'>FIRST OFFICER</td><td>{pilots.fo}</td></tr>
                    {d.fe ? <tr><td className='bold'>FLIGHT ENG</td><td>{pilots.fe}</td></tr> : ''}
                    <tr><td className='bold'>CREW</td><td>{pilots.crew}</td></tr>
                    <tr><td className='bold'>AC</td><td>{d.ac}</td></tr>
                    <tr><td className='bold'>BACKUP</td><td>{d.backup || ''}</td></tr>
                    <tr><td className='bold'>DETAILS</td><td>{d.details || ''}</td></tr>
                    <tr><td className='bold'>CONFIG</td><td>{d.config || ''}</td></tr>
                    <tr><td className='bold'>REMARKS</td><td>{d.remarks || ''}</td></tr>
                    <tr><td className='bold'>SQN PRI</td><td>{d.pri || ''}</td></tr>
                    <tr><td className='bold'>MEAL</td><td>{d.meal || ''}</td></tr>
                    <tr><td className='bold'>DCO</td><td><div className={"dot "+d.dco}></div></td></tr>
                </tbody>
            </table>);
        }
        return (
            <div className={'modal-content'}>
                <div className='buttons'>
                    <img className="edit" src={EDIT} title='Edit'/>
                    <span className="close" title='Close Dialog' onClick={this.props.onClose}>&times;</span>
                </div>
                {content}
            </div>
        )
    }

    componentDidMount() {
        firebase.firestore().collection("events").doc(this.props.id)
        .get()
        .then((querySnapshot) => {
            this.setState({data: querySnapshot.data()})
        })
        .catch(function(error) {
            console.log("Error getting documents: ", error);
        });
    }

    render() {
        /*return (
            <div className="overlay" onClick={this.props.onClose}>
                <div className="overlay-content">
                    <span className="close" onClick={this.props.onClose}>&times;</span>
                    <div className="overlay-title"><h1>Flight Details</h1></div>
                    <FlightForm></FlightForm>
                </div>
            </div>
        )*/
        return (
            <div className="overlay" onClick={this.props.onClose}>
                <div className="overlay-content" onClick={(e)=>{e.stopPropagation()}}>
                    {this.Sidebar()}
                    {this.ModalContent()}
                </div>
            </div>
        )
    }
}

export default Overlay;
