import React from 'react';
import Dashboard from './Dashboard.js';
import Flights from './Flights.js';
import Schedule from "./Schedule.js";
import Add from "./Add.js";
import Login from "./Login.js";
import Overlay from './Overlay.js';
import './App.css';
import DASHBOARD_LOGO from './img/Dashboard.png'
import DASHBOARD_ACTIVE_LOGO from './img/Dashboard_active.png'
import FLIGHTS_LOGO from './img/Flights.png';
import FLIGHTS_ACTIVE_LOGO from './img/Flights_active.png';
import SCHEDULE_LOGO from './img/calendar.png';
import SCHEDULE_ACTIVE_LOGO from './img/calendar_blue.png';
import ADD_LOGO from './img/add.png';
import ADD_ACTIVE_LOGO from './img/add_blue.png';
import LOGOUT_LOGO from './img/logout.png';
import LOGOUT_ACTIVE_LOGO from './img/logout_active.png';
import firebase from './Firebase.js';
import ErrorPage from './ErrorPage.js';

window.onerror = ((error)=>{this.setState({ hasError: true }); console.log("ERROR: ", error)});

function Header(props) {
  var title = props.activePage == 'Dashboard' ? 'Hi, '+props.user.split(" ")[0] : props.activePage
  return <h1>{title + (props.addTab === "" ? "" : " "+props.addTab)}</h1>
}

function NavBarTab(props) {
    return (
      <div className='navBarTab'
        onClick={props.onClick}>
        <img className='icon' src={props.icon} alt=""/>
      </div>
    )
}

class NavBar extends React.Component {

  constructor(props) {
    super(props);
    this.state={hasError: false,}
  }

  changeTab(tabName) {
    if (tabName == "Login") {
      if (!window.confirm("Are you sure you want to log out?")) {
        return;
      } 
    }
    this.props.changeTab(tabName); 
    this.props.updateHeader(tabName=="Add" ? "Flight" : "")
  }

  renderNavBarTab(tabName) {

    const logos = {"Dashboard": DASHBOARD_LOGO, "Flights": FLIGHTS_LOGO, "Schedule": SCHEDULE_LOGO, "Add": ADD_LOGO, "Login": LOGOUT_LOGO}
    const activelogos = {"Dashboard": DASHBOARD_ACTIVE_LOGO, "Flights": FLIGHTS_ACTIVE_LOGO, "Schedule": SCHEDULE_ACTIVE_LOGO, "Add": ADD_ACTIVE_LOGO, "Login": LOGOUT_ACTIVE_LOGO}
    const logo = this.props.activePage === tabName ? activelogos[tabName] : logos[tabName];
    return <NavBarTab tabName={tabName} icon={logo} openNavBar={this.props.openNavBar} onClick={() => {this.changeTab(tabName)}}/>
  }

  componentDidCatch(error, info) {
    // Display fallback UI
    this.setState({ hasError: true });
    console.log(error, info);
  }


  render() {
    if (this.state.hasError) {
      return <ErrorPage/>
    }
    return (
      <div className="navBar" style={this.props.activePage == 'Login' ?{display: 'none'}: {display: 'block'}}>
        <div className='navBarTabs'>
          {this.renderNavBarTab('Dashboard')}
          {this.renderNavBarTab('Flights')}
          {this.renderNavBarTab('Schedule')}
          {this.renderNavBarTab('Add')}
          {this.renderNavBarTab('Login')}
        </div>
      </div>
    );
  }

}

class AppBody extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activePage: "Login",
      //activePage: 'Dashboard',
      user: null,
      addTab: "",
      navTabs: ["Dashboard", "Flights", "Schedule", "Add", "Login"],
      flights: ["Thurs, May 2", "Fri, May 4"],
      showOverlay: false,
      hasError: false,
    };
  }

  componentDidMount() {
    firebase.auth().onAuthStateChanged(
      authUser => {
        authUser && authUser.emailVerified
          ? this.setState({ user: authUser.displayName, activePage: "Dashboard"})
          : this.setState({ user: null });
          console.log(authUser);
      },
    );

  }



  renderNavBar() {
    return <NavBar
      activePage={this.state.activePage} 
      navTabs={this.state.navTabs} 
      changeTab={(tabName)=>{this.changeTab(tabName)}}
      updateHeader={(title)=>{this.updateHeader(title)}}/>
  }

  renderHeader() {
    return <Header activePage = {this.state.activePage} addTab={this.state.addTab} user={this.state.user}/>
  }

  updateHeader(title) {
    this.setState({addTab: title});
  }

  renderOverlay() {
    var content = "";
    if (this.state.showOverlay) {
      content = <Overlay event={this.state.overlayEvent} id={this.state.overlayId} onClose={()=>{this.toggleOverlay('', '')}}/>
    }

    return content;
  }

  shouldComponentUpdate(nextState) {
    return this.state.activePage !== nextState.activePage;
  }

  componentDidCatch(error, info) {
    // Display fallback UI
    this.setState({ hasError: true });
    console.log(error, info);
  }

  toggleOverlay(event, id) {
    const currentState = this.state.showOverlay;
    this.setState({showOverlay: !currentState, overlayEvent: event, overlayId: id});
  }

  changeTab(tabName) {
    this.setState({activePage: tabName});
  }

  renderContent(tabName) {
    switch(tabName) {
      case 'Dashboard': 
        return <Dashboard flights={this.state.flights} toggleOverlay={(type,id)=>{this.toggleOverlay(type, id)}} id={this.state.user}/>
      case 'Flights': 
        return <Flights  toggleOverlay={(id)=>{this.toggleOverlay('flight', id)}}/>
      case "Schedule": 
        return <Schedule toggleOverlay={(event, id)=>{this.toggleOverlay(event, id)}} user={this.state.user}/>
      case "Add": 
        return <Add updateHeader={(title)=>{this.updateHeader(title)}} activeUser={this.state.user}/>
      case "Login": 
        return <Login/>
      default: 
        return <Dashboard flights={this.state.flights} toggleOverlay={(id)=>{this.toggleOverlay('flight', id)}}/>
    }
  }


  render() {

    if (this.state.hasError) {
      return <ErrorPage/>
    }
    return (
      <div className="App">
        {this.renderOverlay()}
        <div className="wrapper">
          <div className="header">
            {this.renderHeader()}
          </div>
            {this.renderNavBar()} 
          <div className="content"> 
            {this.renderContent(this.state.activePage)} 
          </div>
        </div>   
      </div>
    );
  };
}

function App() {

  return (
    <AppBody/>
  );
}

export default App;
