import React from 'react';
import logo, { ReactComponent } from './logo.svg';
import './App.css';
import PLANE_LOGO from './plane.png';

function NavBarIcon(props) {
  return (
      <div 
          className={props.open ? 'navBarIcon open': "navBarIcon"} 
          onClick={props.onClick} >
          <div className="bar1"></div>
          <div className="bar2"></div>
          <div className="bar3"></div>
      </div>
  )
}

function Header(props) {
  return <h1>{props.activeTab}</h1>
}

function NavBarTab(props) {
    return (
      <div className={props.active ? 'navBarTab activeTab' :'navBarTab'}
        onClick={props.onClick}>
        <img className={props.active ? 'icon activeTab' :'icon'} src={PLANE_LOGO} alt=""/>
        <a style={props.active ? {display: visible} : {display: hidden}} href="#">{props.tabName} </a>
      </div>
    )
}

class NavBar extends React.Component {

  constructor(props) {
    super(props);
  }

  renderNavBarIcon() {
    return <NavBarIcon open={this.props.open} onClick={()=> {this.props.toggleOpenNavBar()}}/>;
  }

  renderNavBarTab(tabName) {
    return <NavBarTab tabName={tabName} active={this.props.activeTab === tabName} onClick={() => {this.props.changeTab(tabName); this.props.toggleOpenNavBar()}}/>
  }

  render() {
    /** const tabs = '';
    this.state.navTabs.forEach(tab => {
      tabs = tabs.concat(this.renderNavBarTab(tab));
    });*/
    return (
      <div className={this.props.open ? 'openedNavBar navBar': "navBar"}>
        <div className="navBarIcon">
          {this.renderNavBarIcon()}
        </div>
        <div className={this.props.open ? 'openedNavBarTabs navBarTabs': 'navBarTabs'}>
          {this.renderNavBarTab('Dashboard')}
          {this.renderNavBarTab('Flights')}
        </div>
      </div>
    );
  }
}

class AppBody extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      activeTab: "Dashboard",
      navTabs: ["Dashboard", "Flights"],
    };
  }

  renderNavBar() {
    return <NavBar 
      open={this.state.open} 
      activeTab={this.state.activeTab} 
      navTabs={this.state.navTabs} 
      changeTab={(tabName)=>{this.changeTab(tabName)}}
      toggleOpenNavBar={() => {this.toggleOpenNavBar()}}/>
  }

  renderHeader() {
    return <Header activeTab = {this.state.activeTab}/>
  }

  shouldComponentUpdate(nextState) {
    return this.state.open !== nextState.open;
  }

  toggleOpenNavBar() {
    const currentState = this.state.open;
    this.setState({ open: !currentState });
  };

  changeTab(tabName) {
    this.setState({activeTab: tabName});
    console.log("Horray! New state is: " + tabName);
  }

  render() {
    return (
      <div className="App">
        <div className="header">
          {this.renderHeader()}
        </div>
        {this.renderNavBar()}     
      </div>
    );
  };
}

function App() {
  return (
    <AppBody/>
  );
}

export default App;
