import React from "react";
import "./Overlay.css";
import $ from 'jquery';


Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});


class MeetingForm extends React.Component {
    constructor(props) { 
        super(props);
    }

    componentDidMount() {

        $(document).ready(function () {
            /*==================================================================
            [ Focus Contact2 ]*/
            $('.input').each(function(){
                $(this).on('blur', function(){
                    if($(this).val().trim() != "") {
                        $(this).addClass('has-val');
                    }
                    else {
                        $(this).removeClass('has-val');
                    }
                })
            })

            /*==================================================================
            [ Validate ]*/
            var name = $('.validate-input input[name="name"]');
            var email = $('.validate-input input[name="email"]');
            var message = $('.validate-input textarea[name="message"]');


            $('.validate-form').on('submit',function(){
                var check = true;

                if($(name).val().trim() == ''){
                    showValidate(name);
                    check=false;
                }


                if($(email).val().trim().match(/^([a-zA-Z0-9_\-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([a-zA-Z0-9\-]+\.)+))([a-zA-Z]{1,5}|[0-9]{1,3})(\]?)$/) == null) {
                    showValidate(email);
                    check=false;
                }

                if($(message).val().trim() == ''){
                    showValidate(message);
                    check=false;
                }

                return check;
            });


            $('.validate-form .input').each(function(){
                $(this).focus(function(){
                hideValidate(this);
            });
            });

            function showValidate(input) {
                var thisAlert = $(input).parent();

                $(thisAlert).addClass('alert-validate');
            }

            function hideValidate(input) {
                var thisAlert = $(input).parent();

                $(thisAlert).removeClass('alert-validate');
            }
        });

    }

    render() {
        return (
            <div>
                <form className="form validate-form">
                    <div className="wrap-input validate-input" data-validate="Name is required">
                        <input className="input" type="text" name="remarks"/>
                        <span className="focus-input" data-placeholder="MEETING TITLE"></span>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper validate-input" data-validate="Date is required">
                            <input className="input has-val" type="date" name="name" value={new Date().toDateInputValue()}/>
                            <span className="focus-input" data-placeholder="DATE"></span>
                        </div>
                        <div className="medium-wrapper validate-input" data-validate="ESP is required">
                            <input className="input" type="text" name="esp"/>
                            <span className="focus-input" data-placeholder="START TIME"></span>
                        </div>
                        <div className="medium-wrapper validate-input" data-validate="ESP is required">
                            <input className="input" type="text" name="esp"/>
                            <span className="focus-input" data-placeholder="END TIME"></span>
                        </div>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper" data-validate="ESP is required">
                            <input className="input" type="text" name="esp"/>
                            <span className="focus-input" data-placeholder="LOCATION"></span>
                        </div>
                        <div className="medium-wrapper" data-validate="ESP is required">
                            <input className="input" type="text" name="esp"/>
                            <span className="focus-input" data-placeholder="CONFERENCE LINK"></span>
                        </div>
                    </div>



                    <div className="container-form-btn">
                        <div className="wrap-form-btn">
                            <div className="form-bgbtn"></div>
                            <button className="form-btn">
                                Add Meeting
                            </button>
                        </div>
                    </div>
                </form>

            </div>

        );
    }
}

export default MeetingForm;
