import React from 'react';
import moment from 'moment'
import './Dashboard.css';

function DayHeader(props) {
    return (

        <div className='dayHeader'>
            <h3 id='date'>{props.day + "  "}</h3><h3>{props.date}</h3>
        </div>
    )
}

function DayContents(props) {
    var content = [];
    props.events.forEach(event => {
        var [type, start, end, details] = event;
        content.push(
            <div className={type+'Event'} style={{height: ((end-start)*60)+'px'}}>
                <p>{(start < 10 ? '0' : '')+start*100 + " - " + (end < 10 ? '0' : '')+ end*100}</p>
                <h5 className='name'>{type[0].toUpperCase()+type.slice(1,)}</h5>
                <h6>{details}</h6>
            </div>
        )
    });
    return content;
}

function DayView(props) {
    return (
        <div className='dayView'>
            <DayHeader day={props.day} date={props.date}/>
            <DayContents events={props.events}/>
        </div>
    )
}

function WeekdaysHeader(props) {
    var weekdayShort = moment.weekdaysShort();
    var content = []
    weekdayShort.forEach(day => {
        content.push(<th key={day} className="week-day">
        {day}
       </th>);
    })

    return content;
}

function DaysOfMonth(props) {

    var week = [];
    var startOfWeek = props.curDate.startOf('week');
    for (var i =0; i < 7; i++) {
        week.push(startOfWeek.date());
        startOfWeek.add(1, 'day');
    }

    console.log(week)
    let blanks = [];
    for (let i = 0; i < props.firstDayOfMonth; i++) {
      blanks.push(
        <td className="calendar-day empty">{""}</td>
      );
    }
    let daysInMonth = [];
    for (let d = 1; d <= props.daysInMonth; d++){ 
        let currentDay = week.includes(d) ? "curweek" : "";   
            daysInMonth.push(  
               <td key={d} className={`calendar-day ${currentDay}`}>   
                 {d} 
               </td>);
     }
    var totalSlots = [...blanks, ...daysInMonth];
    let rows = [];
    let cells = [];

    totalSlots.forEach((row, i) => {
        if (i % 7 !== 0 ) {
          cells.push(row); // if index not equal 7 that means not go to next week
        } else {
          rows.push(cells); // when reach next week we contain all td in last week to rows 
          cells = []; // empty container 
          cells.push(row); // in current loop we still push current row to new container
        }
        if (i === totalSlots.length - 1) { // when end loop we add remain date
          rows.push(cells);
        }
      });

      let daysinmonth = rows.map((d, i) => {
        return <tr>{d}</tr>;
      });

      return (
        <table className="calendar-day">
        <thead>
          <tr>{WeekdaysHeader()}</tr>
        </thead>
        <tbody>{daysinmonth}</tbody>
      </table>
      )
}

class WeekCalendar extends React.Component {

    constructor(props) {
        super(props);

    }

    render () {
        var days = ['Mon', 'Tues', 'Wed', 'Thur', 'Fri'];
        var content = [];
        for (var i=0; i < 5; i++) {
            content.push(<DayView day={days[i]} date={this.props.dates[i]} events={this.props.events[i]}/>)
        }
        return content;
    }
}

class MonthCalendar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            date: moment(),
            allmonths :moment.months()
        }
    }

    firstDayOfMonth = () => {
        let dateObject = this.state.date;
        let firstDay = moment(dateObject)
                     .startOf("month")
                     .format("d"); 
       return firstDay;
    };

    month = () => {return this.state.date.format("MMMM");};

    MonthList = props => {let months = [];
        props.data.map(data => {
          months.push(
            <td>
              <span>{data}</span>
            </td>
          );
        });
        let rows = [];
        let cells = [];

        months.forEach((row, i) => { 
            if (i % 3 !== 0 || i == 0) { // except zero index 
                cells.push(row); 
            } else { 
                rows.push(cells); 
                cells = [];
                cells.push(row); 
            }
         });
         rows.push(cells); // add last row

         let monthlist = rows.map((d, i) => {
            return <tr>{d}</tr>;
         });
         return (
            <table className="calendar-month">
              <thead>
                <tr>
                  <th colSpan="4">Select a Month</th>
                </tr>
              </thead>
              <tbody>{monthlist}</tbody>
            </table>
          );
        }

    setMonth = month => {
        let monthNo = this.months.indexOf(month);// get month number 
        let dateObject = Object.assign({}, this.state.date);
        dateObject = moment(dateObject).set("month", monthNo); // change month value
        this.setState({
            date: dateObject // add to state
        });
        this.props.data.map(data => {
            this.months.push(
              <td
                key={data}
                className="calendar-month"
                onClick={e => {
                  this.setMonth(data);
                }}
              >
                <span>{data}</span>
              </td>
            );
          });
    };

    render () {

        var firstDay = this.firstDayOfMonth();
        var daysInMonth = this.state.date.daysInMonth();
        return (
        <div>
            <div className="tail-datetime-calendar">
                <div className="calendar-navi">{this.month()}</div>
                <div className="calendar-date">
   <this.MonthList data={moment.months()} />
</div>
            </div>
            <DaysOfMonth curDate={this.state.date} firstDayOfMonth={firstDay} daysInMonth={daysInMonth}/>
        </div>)
    }
}

export default WeekCalendar;
export {MonthCalendar};
