import React from 'react';
import "./Flights.css";
import RIGHT_BTN from './img/right-arrow.png';
import LEFT_BTN from './img/left-arrow.png';
import firebase from "./Firebase.js";

var today = new Date();
var dd = String(today.getDate()).padStart(2, '0');
var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
var yyyy = today.getFullYear();

today = mm + '/' + dd + '/' + yyyy;

/** var data = []
firebase.collection("flights").get().then(querySnapshot => {
querySnapshot.docs.map(doc => {
    console.log(doc)
});})*/



function date(curDate) {
    //var today = new Date();
    var weekday = curDate.getDay();
    var day = curDate.getDate();
    var month = curDate.getMonth();
    var year = curDate.getFullYear();
    var days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    var months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November","December"];
    return (
        <div className='flight-date'>
            <h2>{days[weekday] +", "+months[month]+" "+day+", "+year}</h2>
        </div>
    )
}

function RenderFlightDayHeader(props) {
    var displayDate = new Date(props.date);
    var curDate = new Date();
    var diff = Math.round((displayDate.getTime() - curDate.getTime())/(24*60*60*1000));

    // Only show flights for last 3 days to the next 2 weeks
    var disableLeft = diff == -3;
    var disableRight= diff == 14;

    return (
        <div className="flight-day-header">
            <img className={'date-btn' + (disableLeft ? ' disabled': '')} src={LEFT_BTN} onClick={()=>{props.changeDate(disableLeft ? 0 : -1)}}></img>
            {date(displayDate)}
            <img className={'date-btn' + (disableRight ? ' disabled': '')} src={RIGHT_BTN} onClick={()=>{props.changeDate(disableRight ? 0 : 1)}}></img>
        </div>
    )
}


function RenderFlightDay(props) {

    var headers = ['ETD', 'AC', 'CREW', 'MISSION', 'DETAILS', 'CONFIG', 'REMARKS', 'SQ PRI', 'DCO', 'ETR'];
    var tableHeaders = [];
    var ac = <div className='ac-wrap'>462</div>;
    var dot = <div className="dot-ok"></div>;
    headers.forEach(title => {
        tableHeaders.push(<th>{title}</th>)
    });
    var data = props.data != null ? props.data : [['0900', ac, 'Amelia E | Charlies L', '427 AU H102 G00', 'SME', 'DC6S', "", '1 SOA', dot, '1030'], 
                ['2520', ac, 'D.B. Cooper | Maverick', '427 AU H105 G00', 'ALP1', 'DC6S', "", '3 SOA', dot, '1230']]

    var tableRows = [];

    data.forEach(row => {
        var r = [];
        if (data != props.data) {
            for (var i = 0; i<row.length; i++) {
                r.push(<td className={headers[i].toLowerCase()}>{row[i]}</td>);
            }  
            var hr = parseInt(row[0][0]+row[0][1]);
            var min = parseInt(row[0][2]+row[0][3]);
        }
        else {
            console.log(data);
        }
        tableRows.push(<tr className={(props.curTime.getHours() > hr) || (hr == props.curTime.getHours() && props.curTime.getMinutes() > min) ? 'expired':''} onClick={props.toggleOverlay.bind(this,'521512')}>{r}</tr>);
    });
    return (
        <div className="flight-day">
            <table>
                <tr>
                    {tableHeaders}
                </tr>
                {tableRows}
            </table>
        </div>
    )
}

function RenderFlightDayBox(props) {
    return (
        <div className="flight-day-box">
            <RenderFlightDayHeader date={props.date} changeDate={(offset)=>{props.changeDate(offset)}}/>
            <RenderFlightDay date={props.date} data={props.data} curTime={props.curTime} toggleOverlay={(id)=>{props.toggleOverlay(id)}}/>
        </div>
    )
}

function RenderInfoBox(props) {
    return (
        <div className="info-box">
            <div className="info-title">
                <h5>{props.title}</h5>
            </div>
            <div className="info-content" contentEditable={true} suppressContentEditableWarning={true}>
                <h5>N/A</h5>
            </div>
        </div>
    )
}

function RenderInfoBar(props) {
    const default_info = ["EVENING TWILIGHT", "SNAGS DESK SGT", "DUTY MVP", "FDO", "DUTY GROUND RUN"];
    var boxes = [];
    for (var i = 0; i < default_info.length; i++) {
        var title = default_info[i]
        boxes.push(<RenderInfoBox title={title}/>)
    }
    return (
        <div className="info-bar">
            <div className="time-title">
                <h5>CURRENT TIME</h5>
            </div>
            <div className="time">
                <h2>{(props.curTime.getHours()<10?"0":"")+props.curTime.getHours()+':'+(props.curTime.getMinutes()<10?"0":"")+props.curTime.getMinutes()}</h2>
            </div>
            {boxes}
        </div>
    )
}

class Flights extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            curTime: new Date(),
            date: new Date(),
            data: null,
        }
    }
    componentDidMount() {
        this.timerID = setInterval(() => this.tick(), 1000);
        this.getData(new Date());
    }

    componentWillUnmount() {clearInterval(this.timerID);}

    tick() {this.setState({curTime: new Date()});}

    getData() {
        var newDate = new Date(this.state.date);
        var date = (newDate.getMonth() < 9 ? '0':'') + (newDate.getMonth()+1) + '-' + (newDate.getDate() < 9 ? '0':'') + (newDate.getDate()+1) + '-' + newDate.getFullYear();
        //get new data
        firebase.firestore().collection("events").where('type', '==', 'flight').where('date', '==', date)
        .get()
        .then((querySnapshot) => {
            if (!querySnapshot.empty) {
                this.setState({data: querySnapshot.data()})
            }
            else {
                console.log("Document doesn't exist");
                this.setState({data: null});
            };
        })
        .catch(function(error) {
            console.log("Error getting documents: ", error);
        });
    }

    changeDate(offset) {
        if (offset != 0) {
            var newDate = new Date(this.state.date);
            newDate.setDate(newDate.getDate() + offset);
            this.setState({date: newDate})
            this.getData();
        }
    }

    render() {
        return (
            <div className='container'>
                <RenderFlightDayBox date={this.state.date} 
                                    data={this.state.data}
                                    curTime={this.state.curTime} 
                                    toggleOverlay={(id)=>{this.props.toggleOverlay(id)}} 
                                    changeDate={(offset)=>{this.changeDate(offset)}}/>
                <RenderInfoBar date={this.state.date} curTime={this.state.curTime}/>
            </div>
        )
    }
}

export default Flights;
