import XLSX from 'xlsx';
import firebase from "./Firebase.js";

const sheetHeader =["DAILY FLYING ORDER", "","","","","","","","","","","","","","","",""];
const emptyCells = ["","","","","","",""];
const flightHeaders = ["startTime","endTime","A/C","BACKUP","captain","fo","fe","crew","config","details","mission","details","config","remarks","pri","meal","DCO"];
var date = [];

function s2ab(s) {
    var buf = new ArrayBuffer(s.length);
    var view = new Uint8Array(buf);
    for (var i=0; i<s.length; i++) view[i] = s.charCodeAt(i) & 0xFF;
    return buf;
}

const download = (url, name) => {
    let a = document.createElement('a')
    a.href = url
    a.download = name
    a.click()

    window.URL.revokeObjectURL(url)
}

function getExportData(ids, dayInfo) {
    //Gathering all data: 
    var flights = [sheetHeader, flightHeaders];
        firebase.firestore().collection("events").where(firebase.firestore.FieldPath.documentId(), 'in', ids)
            .get()
            .then((querySnapshot) => {
                querySnapshot.docs.forEach((d) => {
                    var d = d.data();
                    date = d.date;
                    flights.push([
                        d.startTime,
                        d.endTime,
                        d.ac,
                        d.backup,
                        d.captain,
                        d.fo,
                        d.fe,
                        d.crew,
                        d.config,
                        d.details,
                        d.mission,
                        d.details,
                        d.config,
                        d.remarks,
                        d.pri,
                        d.meal,
                        d.dco,]);
                    })

                    var day = [];
                    //Get Day info 
                    firebase.firestore().collection("dates").doc(date).get()
                    .then((querySnapshot) => {
                        var d = querySnapshot.data();
                        day.push(["DATE: " + date, emptyCells, "SDO: ", emptyCells, ""]);
                        day.push(["TWILIGHT CIV: " + d.twilightciv, emptyCells, "TWILIGHT NAUT: "+d.twilightnaut, emptyCells, ""]);
                        day.push(["DAY FDO: " + d.fdoday, emptyCells, "NIGHT FDO: "+d.fdonight, emptyCells, ""]);
                        day.push(["DESK SGT DAY: " + d.desksgtday, emptyCells, "DESK SGT NIGHT: "+d.desksgtnight, emptyCells, ""]);
                        day.push(["MTP: " + d.mtp, emptyCells, "GROUND RUN: "+d.groundrun, emptyCells, ""]);

                        Export(flights, day);
                    })
                    .catch(function(error) {
                        console.log("Error getting data: ", error);
                    });

                })

            .catch(function(error) {
                console.log("Error getting data: ", error);
            });



}

function Export(flightData, dayData) {
    var exportData = [flightData, dayData];
    /* convert from array of arrays to worksheet */
    var worksheet = XLSX.utils.aoa_to_sheet(exportData);

    /* merge cells A1:B1 */
    var merge = XLSX.utils.decode_range("A1:Q1");

    /* add merges */
    if(!worksheet['!merges']) worksheet['!merges'] = [];
    worksheet['!merges'].push(merge);

    /** generate workbook */
    var new_workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(new_workbook, worksheet, date);

    /* write file */
    const wbout = XLSX.write(new_workbook, {type:'binary', bookType:"xlsx"});

    let url = window.URL.createObjectURL(new Blob([s2ab(wbout)], {type:'application/octet-stream'}))

    download(url, 'DailyFlyingOrder.xlsx')

}

export default Export;
