import React from 'react';
import Dashboard from './Dashboard.js';
import Flights from './Flights.js';
import Schedule from "./Schedule.js";
import Add from "./Add.js";
import Login from "./Login.js";
import FlightForm from './FlightForm.js';
import Overlay from './Overlay.js';
import './App.css';
import DASHBOARD_LOGO from './img/Dashboard.png'
import DASHBOARD_ACTIVE_LOGO from './img/Dashboard_active.png'
import FLIGHTS_LOGO from './img/Flights.png';
import FLIGHTS_ACTIVE_LOGO from './img/Flights_active.png';
import SCHEDULE_LOGO from './img/calendar.png';
import SCHEDULE_ACTIVE_LOGO from './img/calendar_blue.png';
import ADD_LOGO from './img/add.png';
import ADD_ACTIVE_LOGO from './img/add_blue.png';
import firebase from './Firebase.js';

function Header(props) {
  var title = props.activePage == 'Dashboard' ? 'Hi, '+props.user.split(" ")[0] : props.activePage
  return <h1>{title + (props.addTab === "" ? "" : " "+props.addTab)}</h1>
}

function NavBarTab(props) {
    return (
      <div className='navBarTab'
        onClick={props.onClick}>
        <img className='icon' src={props.icon} alt=""/>
      </div>
    )
}

class NavBar extends React.Component {

  constructor(props) {
    super(props);
  }

  renderNavBarTab(tabName) {

    const logos = {"Dashboard": DASHBOARD_LOGO, "Flights": FLIGHTS_LOGO, "Schedule": SCHEDULE_LOGO, "Add": ADD_LOGO, "Login": DASHBOARD_LOGO}
    const activelogos = {"Dashboard": DASHBOARD_ACTIVE_LOGO, "Flights": FLIGHTS_ACTIVE_LOGO, "Schedule": SCHEDULE_ACTIVE_LOGO, "Add": ADD_ACTIVE_LOGO, "Login": DASHBOARD_ACTIVE_LOGO}
    const logo = this.props.activePage === tabName ? activelogos[tabName] : logos[tabName];
    return <NavBarTab tabName={tabName} icon={logo} openNavBar={this.props.openNavBar} onClick={() => {this.props.changeTab(tabName); this.props.updateHeader(tabName=="Add"?"Flight":"")}}/>
  }

  render() {
    /** const tabs = '';
    this.state.navTabs.forEach(tab => {
      tabs = tabs.concat(this.renderNavBarTab(tab));
    });*/
    return (
      <div className="navBar" style={this.props.activePage == 'Login' ?{display: 'none'}: {display: 'block'}}>
        <div className='navBarTabs'>
          {this.renderNavBarTab('Dashboard')}
          {this.renderNavBarTab('Flights')}
          {this.renderNavBarTab('Schedule')}
          {this.renderNavBarTab('Add')}
          {this.renderNavBarTab('Login')}
        </div>
      </div>
    );
  }

}

class AppBody extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      //activePage: "Login",
      activePage: "Schedule",
      user: null,
      addTab: "",
      navTabs: ["Dashboard", "Flights", "Schedule", "Add", "Login"],
      flights: ["Thurs, May 2", "Fri, May 4"],
      showOverlay: false,
    };
  }

  componentDidMount() {
    firebase.auth().onAuthStateChanged(
      authUser => {
        authUser && authUser.emailVerified
          ? this.setState({ user: authUser.displayName, activePage: "Dashboard"})
          : this.setState({ user: null });
          console.log(this.state.user);
      },
    );
  }

  renderNavBar() {
    return <NavBar
      activePage={this.state.activePage} 
      navTabs={this.state.navTabs} 
      changeTab={(tabName)=>{this.changeTab(tabName)}}
      updateHeader={(title)=>{this.updateHeader(title)}}/>
  }

  renderHeader() {
    return <Header activePage = {this.state.activePage} addTab={this.state.addTab} user={this.state.user}/>
  }

  updateHeader(title) {
    this.setState({addTab: title});
  }

  renderOverlay(page) {
    var content = "";
    if (this.state.showOverlay) {
        content = <Overlay page={page} onClose={()=>{this.toggleOverlay()}}/>
    }

    return (
      <div className = {this.state.showOverlay ? 'overlay' : null}>
        <div className = {this.state.showOverlay ? 'overlayContent' : null}>
          {content}
        </div>
      </div>);
  }

  shouldComponentUpdate(nextState) {
    return this.state.activePage !== nextState.activePage;
  }

  toggleOverlay() {
    const currentState = this.state.showOverlay;
    this.setState({showOverlay: !currentState});
  }

  changeTab(tabName) {
    this.setState({activePage: tabName});
  }

  renderContent(tabName) {
    switch(tabName) {
      case 'Dashboard': 
        return <Dashboard flights={this.state.flights} toggleOverlay={()=>{this.toggleOverlay()}}/>
      case 'Flights': 
        return <Flights/>
      case "Schedule": 
        return <Schedule/>
      case "Add": 
        return <Add updateHeader={(title)=>{this.updateHeader(title)}}/>
      case "Login": 
        return <Login/>
      default: 
        return <Dashboard flights={this.state.flights} toggleOverlay={()=>{this.toggleOverlay()}}/>
    }
  }


  render() {
    return (
      <div className="App">
        {this.renderOverlay()}
        <div className="wrapper">
          <div className="header">
            {this.renderHeader()}
          </div>
            {this.renderNavBar()} 
          <div className="content"> 
            {this.renderContent(this.state.activePage)} 
          </div>
        </div>   
      </div>
    );
  };
}

function App() {
  return (
    <AppBody/>
  );
}

export default App;
export {Header};
