import React from 'react';
import './index.css';
import './Dashboard.css';
import ADD_LOGO from './img/add.png';

function FlightCard(props) {
    return (
        <div className="flightCard" onClick={props.toggleOverlay}>
            <div className="date">
                <h2>{props.flight}</h2>
                <p>9:00 - 12:00</p>
            </div>
            <hr/>
            <div className="infoLine">
                <div className="title">
                    <h5>CAPT</h5>
                </div>
                <div className="text">
                    <h5>Amelia Earheart</h5>
                </div>
            </div>
            <div className="infoLine">
                <div className="title">
                    <h5>FO</h5>
                </div>
                <div className="text">
                    <h5>Charles Hindbergh</h5>
                    <h5>D.B. Cooper</h5>
                </div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>A/C</h5></div>
                <div className="text"><h5>483</h5></div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>TYPE</h5></div>
                <div className="text"><h5>ALP 1</h5></div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>CONFIG</h5></div>
                <div className="text"><h5>DC6S</h5></div>
            </div>
        </div>
    );
}

function renderWeek(props) {
    return (
        <div class="cd-schedule__events">
            <ul>
            <li class="cd-schedule__group">
                <div class="cd-schedule__top-info"><span>Monday</span></div>

                <ul>
                <li class="cd-schedule__event">
                    <a data-start="09:30" data-end="10:30" data-content="event-abs-circuit" data-event="event-1" href="#0">
                    <em class="cd-schedule__name">Abs Circuit</em>
                    </a>
                </li>

                </ul>
            </li>

            <li class="cd-schedule__group">
                <div class="cd-schedule__top-info"><span>Tuesday</span></div>

                <ul>
                </ul>
            </li>

            </ul>
        </div>
    )
}


function renderMonth(props) {
    return (
        <p>Hello World! Month</p>
    )
}

class Dashboard extends React.Component {

    constructor(props) {
        super(props);
    }

    renderUpcomingEvents() {
        return (
            <div className="upcomingEvents">
                <div className="weekView">
                    {renderWeek()}
                </div>
                <div className="monthView">
                    {renderMonth()}
                </div>
            </div>
        )
    }

    renderUpcomingFlights() {
        var cards = []
        this.props.flights.forEach(flight => {
            cards.push(<FlightCard flight={flight} toggleOverlay={()=>{this.props.toggleOverlay('AC250', '7893521')}}/>)
        });
        return cards
    }
    render() { 
        return (
            <div>
                <h2>My Upcoming Flights</h2>
                <div className="upcomingFlights">
                    {this.renderUpcomingFlights()}
                </div>
                <h2>My Schedule</h2>
                {this.renderUpcomingEvents()}
            </div>

        )
    };
}

export default Dashboard;
