function parseID(name) {
    //aearhart to A. Earhart 
    if (name) {
    return name[0].toUpperCase() + ". " + name[1].toUpperCase() + name.slice(2);}
}

function parseName(name) {
    //Amelia Earhart to aearhart
    if (name) {
    return name[0].toUpperCase() + ". " + name[1].toUpperCase() + name.slice(2);}
}
