import Login from "./Login";
import React from 'react';
import ReactGA from 'react-ga';
import firebase from "./Firebase.js";
import ErrorPage from './ErrorPage.js';
import './Personnel.css';

function initializeReactGA() {
    ReactGA.initialize('UA-108277898-5');
    ReactGA.pageview('/Personnel');
}

function PersonnelInfo(props) {
    return (
        <div>
            <div className="personnelInfo">
                <RenderInfo user={props.user} team={props.team} name={props.name} teamNum={props.teamNum} lastFlight={props.lastFlight} role={props.role} email={props.email}/>
            </div>
            <RenderLogoutBtn/>
            <Approvals role={props.role}/>
        </div>
    )
}

function RenderInfo(props) {
    return (
        <div>
            <div className="myInfo">
                <div className="infoField">
                    <h5>UserID</h5>
                    <input className='pinput' id="userid" defaultValue={props.user} type="text"/>
                </div>
                <div className="infoField">
                    <h5>Name</h5>
                    <input className='pinput' id="name" defaultValue={props.name} type="text"/>
                </div>
                <div className="infoField">
                    <h5>Last Flight</h5>
                    <input className='pinput' id="lastFlight" defaultValue={props.lastFlight} type="date"/>
                </div>
                <div className="infoField">
                    <h5>Role</h5>
                    <input className='pinput' id="role" defaultValue={props.role} type="text"/>
                </div>
                <div className="infoField">
                    <h5>Email</h5>
                    <input className='pinput' id="useremail" defaultValue={props.email} type="text"/>
                </div>
                <div className="infoField">
                    <h5>Team Number</h5>
                    <input className='pinput' id="teamNum" defaultValue={props.teamNum} type="text"/>
                </div>
            </div>
            <div className="teamInfo">
                <TeamInfo team={props.team}/>
            </div>
        </div>
    )
}

function TeamInfo(props) {
    var team = [];

    props.team.forEach((member) => {
        team.push(<tr>{parseID(member)}</tr>)
    }) 

    return (
        <table className="teamTable">
            <tr><th>Team Members</th></tr>
            {team}
        </table>
    )
}

function Approvals(props) {
    props.approvals.forEach((pilot))
    if (props.role == 'admin') {
        return (
            <table className="approvalsTable">
            <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Approve</th>
            </tr>
            <tr><td>Jimmy Doolittle</td><td>jdoolittle@forces.gc.ca</td><td>Pilot</td><td><RenderApproveBtn/></td></tr>
            <tr><td>Orvill Wright</td><td>owright@forces.gc.ca</td><td>Staff</td><td><RenderApproveBtn/></td></tr>
            <tr><td>Wilbur Wright</td><td>wwright@forces.gc.ca</td><td>Staff</td><td><RenderApproveBtn/></td></tr>
            <tr><td>Chesley Sullenberger</td><td>csullenberger@forces.gc.ca</td><td>Pilot</td><td><RenderApproveBtn/></td></tr>
            <tr><td>Bessie Coleman</td><td>bcoleman@forces.gc.ca</td><td>Staff</td><td><RenderApproveBtn/></td></tr>
            </table>
        )
    }

    return <div></div>;
}

function RenderApproveBtn() {
    return (
        <div className='approve-btn-div '>
                <button className="approve-btn" type="button" onClick={((e) => {e.target.className += ' approved'})}>
                    Approve
                </button>
        </div>
    );
}


function RenderLogoutBtn() {
    return (
        <div className='logout-btn-div '>
                <button className="logout-btn" type="button" onClick={logOut}>
                    Log Out
                </button>
        </div>
    );
}

function logOut() {
    if (!window.confirm("Are you sure you want to log out?")) {
        return;
      } 
    return <Login/>
}

function parseID(id) {
    //aearhart to A. Earhart 
    if (id) {
    return id[0].toUpperCase() + ". " + id[1].toUpperCase() + id.slice(2);}
}

function parseName(name) {
    //Amelia Earhart to aearhart
    if (name) {
    return name[0].toLowerCase() + name.slice(name.indexOf(' ')+1).toLowerCase();}
}




class Personnel extends React.Component{
    constructor(props) {
        super(props)
        /* name */
        this.state={
            userID: '',
            team: [],
            teamNum: null,
            lastFlight: null,
            email: '',
            hasError: false,
            approvals: [],
        };
    }
    componentDidCatch(error, info) {
        // Display fallback UI
        this.setState({ hasError: true });
        console.log(error, info);
    }

    componentDidMount() {
        initializeReactGA();
        this.getTeamMembers();
        var id = parseName(this.props.name);
        this.setState({userID: id});
        this.getApprovals();
    }

    getApprovals() {
        firebase.firestore().collection("pilots")
        .get()
        .then((querySnapshot) => {
            var approvals = []
            querySnapshot.forEach((pilot)=> {
                approvals.push({firstName: pilot.firstName, 
                            lastName: pilot.lastName, 
                            email: pilot.email, 
                            role: pilot.role, 
                            approved: pilot.approved})
            })
            this.setState({approvals: approvals});
        })
        .catch(function(error) {
            console.log("This user doesn't exist: ", error);
        });
    }

    getTeamMembers() {
        var user = parseName(this.props.name);
        firebase.firestore().collection("pilots").doc(user)
        .get()
        .then((querySnapshot) => {
            if (!querySnapshot.empty) {
                var lastFlight = querySnapshot.data().lastFlight;
                var email = querySnapshot.data().email;
                var num = querySnapshot.data().team;
                var role = querySnapshot.data().role;
                this.setState({teamNum: num, lastFlight: lastFlight, email: email, role: role});
                firebase.firestore().collection("pilots").doc(num.toString())
                .get()
                .then((querySnapshot) => {
                    if (!querySnapshot.empty && querySnapshot.data()) {
                        this.setState({team: querySnapshot.data().pilots});
                    }
                    else {
                        console.log("Document doesn't exist");
                        this.setState({team: null});
                    };
                });  
            }
            else {
                console.log("Document doesn't exist");
                this.setState({team: null});
            };
        })
        .catch(function(error) {
            console.log("This user doesn't exist: ", error);
        });
    }

    render() {
        if (this.state.hasError) {
            return <ErrorPage/>
        }
        return (
            <PersonnelInfo user={this.state.userID} name={this.props.name} role={this.state.role} team={this.state.team} teamNum={this.state.teamNum} 
                            lastFlight={this.state.lastFlight}
                            email={this.state.email} approvals={this.state.approvals}/>
        )
    }
}

export default Personnel;
