import React from 'react';
import "./Schedule.css";
import RIGHT_BTN from './img/right-arrow.png';
import LEFT_BTN from './img/left-arrow.png';

var today = new Date();
var dd = String(today.getDate()).padStart(2, '0');
var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
var yyyy = today.getFullYear();

today = mm + '/' + dd + '/' + yyyy;

function date(props) {
    var today = new Date();
    var weekday = today.getDay();
    var day = today.getDate();
    var month = today.getMonth();
    var year = today.getFullYear();
    var days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
    var months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November","December"];
    return (
        <div className='flight-date'>
            <h2>{days[weekday] +", "+months[month]+" "+day+", "+year}</h2>
        </div>
    )
}

function renderDayHeader(props) {
    return (
        <div className="day-header">
            <img className='date-btn' src={LEFT_BTN}></img>
            {date()}
            <img className='date-btn' src={RIGHT_BTN}></img>
        </div>
    )
}

function Day(props) {
    var data = [['Amelia Earhart', [['flight', 2, 5], ['flight', 8, 9], ['meeting', 12, 15]]], 
    ['D.B. Cooper', [['meeting', 4, 6], ['flight', 10, 14]]], 
    ['Pete Mitchell', [['apt', 8, 9], ['flight', 14, 17], ['meeting', 19, 21]]], 
    ['Goose', [['flight', 4, 8], ['meeting', 9, 10], ['apt', 17, 18]]], 
    ['Maverick', [['apt', 4, 5], ['flight', 12, 14]]], 
    ['Charles Lindbergh', [['meeting', 5, 6], ['apt', 14, 17], ['meeting', 19, 20]]], 
    ['Iceman', [['flight', 2, 5], ['flight', 8, 9], ['meeting', 12, 15]]], 
    ['Jenny', [['meeting', 4, 6], ['flight', 10, 14]]], 
    ['Pete Mitchell', [['apt', 8, 9], ['flight', 14, 17], ['meeting', 19, 21]]], 
    ['Goose', [['flight', 4, 8], ['meeting', 9, 10], ['apt', 17, 18]]], 
    ['Maverick', [['apt', 4, 5], ['flight', 12, 14]]], 
    ['Charles Lindbergh', [['meeting', 5, 6], ['apt', 14, 17], ['meeting', 19, 20]]]];
    var tableRows = [];

    var tableHeaders = [];
    tableHeaders.push(<th></th>);
    for (var i = 0; i < 24 ; i += 2) {
        tableHeaders.push(<th>{(i < 10 ? '0'+i.toString() : i.toString())+'00'}</th>);
    }
    var rowTemplate = [];
    for (var i = 0; i < 12 ; i += 1) {
        rowTemplate.push(<td></td>);
    }

    data.forEach(row => {
        var r = [];
        r.push(<td className='pilot-col'>{row[0]}</td>);
        r.push(rowTemplate);
        row[1].forEach(event => {
            var [name, left, right] = event;
            var style = {width: (right-left)+'%'};
            r[left] = (<td><button className={name} style={style}/></td>);
        })
        tableRows.push(<tr>{r}</tr>);
    });

    /*data.forEach(row => {
        var r = [];
        r.push(<td className='pilot-col'>{row[0]}</td>);

        var events = [];
        var offset = 0;
        for (var i = 0; i<row[1].length; i++) {
            var [name, left, right] = row[1][i];

            var style = {width: (right-left)*45, left: (left*45-offset)};

            offset += (right-left)*45
            events.push(<button className={name} style={style}/>);
        }  
        r.push(<td className='event-col'>{events}</td>);
        tableRows.push(<tr>{r}</tr>);
    });*/
    return (
        <div className="day-container">
            <table>
                <tr>
                    {tableHeaders}
                </tr>
                {tableRows}
            </table>
        </div>

    )
}

function Gridline(props) {
    return (
        <div className="gridline">
            <h5 className='hour'>{props.hour+'00'}</h5>
            <div className="line"/>
        </div>
    )
}

function Grid(props) {
    var lines = [];
    for (var i = 0; i < 24 ; i += 2) {
        lines.push(<Gridline hour={i < 10 ? '0'+i.toString() : i.toString()}/>)
    }
    return (
        <div className="grid-container">
            {lines}
        </div>
    )
}

function Legend(props) {
    return (
        <div className="legend-container">
            <div className="legend-color flight"></div>
            <h4 className="legend-entry">Flight</h4>
            <div className="legend-color apt"></div>
            <h4 className="legend-entry">Appointment</h4>
            <div className="legend-color meeting"></div>
            <h4 className="legend-entry">Meeting</h4>
        </div>
    )
}


class Schedule extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        }
    }
    render() {
        return (
            <div className='page-container'>
                {renderDayHeader()}

                <Day/>
                <Legend/>
            </div>
        )
    }
}

export default Schedule;
