import React from 'react';
import './Dashboard.css';


function DayHeader(props) {
    return (

        <div className='dayHeader'>
            <h3>{props.day + " " + props.date}</h3>
        </div>
    )
}

function DayContents(props) {
    var content = [];
    for (var i=0; i < props.events.length; i++) {
        var [type, start, end, details] = props.events[i];
        content.push(
            <div className={type+'Event'} style={{height: ((end-start)*60-(i*5))+'px'}}>
                <p>{(start < 10 ? '0' : '')+start*100 + " - " + (end < 10 ? '0' : '')+ end*100}</p>
                <h5 className='name'>{type[0].toUpperCase()+type.slice(1,)}</h5>
                <h6>{details}</h6>
            </div>
        )
    };
    return content;
}

function DayView(props) {
    return (
        <div className='dayView'>
            <DayHeader day={props.day} date={props.date}/>
            <DayContents events={props.events}/>
        </div>
    )
}

class Calendar extends React.Component {

    constructor(props) {
        super(props);
    }

    render () {
        var days = ['Mon', 'Tues', 'Wed', 'Thur', 'Fri'];
        var content = [];
        for (var i=0; i < 5; i++) {
            content.push(<DayView day={days[i]} date={this.props.dates[i]} events={this.props.events[i]}/>)
        }
        return content;
    }
}

export default Calendar;
