import React from 'react';
import "./Overlay.css";

import FlightForm from "./FlightForm.js";
import EDIT from './img/edit.png';
import EDIT_HOVER from './img/edit-hover.png';



class Overlay extends React.Component {

    constructor(props) {
        super(props);
    }

    Sidebar() {
        var content = ""
        return (
            <div className={'sidebar '+'flight-event'}>
                <h4>Thurs, May 2, 2019</h4>
                <div className='sidebar-content'>
                    <h4>1000 - 1300</h4>
                    <h1>SME</h1>
                </div>
            </div>
        )
    }
    ModalContent() {
        var content = ""
        //if (props.eventType=='flight-event') {
            content = (
            <table>
                <tr><td className='bold'>CAPTAIN</td><td>{'Amelia Earhart'}</td></tr>
                <tr><td className='bold'>FIRST OFFICER</td><td>{'Amelia Earhart'}</td></tr>
                <tr><td className='bold'>FLIGHT ENG</td><td>{'Amelia Earhart'}</td></tr>
                <tr><td className='bold'>CREW</td><td>{'Amelia Earhart'}</td></tr>
                <tr><td className='bold'>AC</td><td>{'Amelia Earhart'}</td></tr>
                <tr><td className='bold'>BACKUP</td><td>{'Amelia Earhart'}</td></tr>
                <tr><td className='bold'>DETAILS</td><td>{'Amelia Earhart'}</td></tr>
                <tr><td className='bold'>CONFIG</td><td>{'Amelia Earhart'}</td></tr>
                <tr><td className='bold'>REMARKS</td><td>{'Amelia Earhart'}</td></tr>
                <tr><td className='bold'>SQN PRI</td><td>{'Amelia Earhart'}</td></tr>
                <tr><td className='bold'>MEAL</td><td>{'Amelia Earhart'}</td></tr>
                <tr><td className='bold'>DCO</td><td>{'Amelia Earhart'}</td></tr>
            </table>);
        //}
        return (
            <div className={'modal-content'}>
                <img className="edit" src={EDIT}/>
                <span className="close" onClick={this.props.onClose}>&times;</span>
                {content}
            </div>
        )
    }

    render() {
        /*return (
            <div className="overlay" onClick={this.props.onClose}>
                <div className="overlay-content">
                    <span className="close" onClick={this.props.onClose}>&times;</span>
                    <div className="overlay-title"><h1>Flight Details</h1></div>
                    <FlightForm></FlightForm>
                </div>
            </div>
        )*/
        var event = {time: '1000 - 1400', title: 'SME2'}
        return (
            <div className="overlay" onClick={this.props.onClose}>
                <div className="overlay-content" onClick={(e)=>{e.stopPropagation()}}>
                    {this.Sidebar()}
                    {this.ModalContent()}
                </div>
            </div>
        )
    }
}

export default Overlay;
