import React from 'react';
import "./Overlay.css";

import FlightForm from "./FlightForm.js";

function Sidebar(props) {
    var content = ""
    return (
        <div className={'sidebar '+props.eventType}>
            <h5>{props.event.time}</h5>
            <h3>{props.event.title}</h3>
        </div>
    )
}
function ModalContent(props) {
    var content = ""
    if (props.eventType=='flight-event') {
        content = (
        <table>
            <tr><td className='bold'>Crew</td><td>{'Amelia Earhart'}</td></tr>
            <tr><td className='bold'>Crew</td><td>{'Amelia Earhart'}</td></tr>
        </table>);
    }
    return (
        <div className={'modal-content '+props.eventType}>
            <span className="close" onClick={props.onClose}>&times;</span>
            {content}
        </div>
    )
}

class Overlay extends React.Component {

    constructor(props) {
        super(props);
    }

    render() {
        /*return (
            <div className="overlay" onClick={this.props.onClose}>
                <div className="overlay-content">
                    <span className="close" onClick={this.props.onClose}>&times;</span>
                    <div className="overlay-title"><h1>Flight Details</h1></div>
                    <FlightForm></FlightForm>
                </div>
            </div>
        )*/
        var event = {time: '1000 - 1400', title: 'SME2'}
        return (
            <div className="overlay" onClick={this.props.onClose}>
                <div className="overlay-content" onClick={(e)=>{e.stopPropagation()}}>

                    <Sidebar eventType='flight-event'/>
                    <ModalContent eventType='flight-event' event={event} onClose={()=>{this.props.onClose()}}></ModalContent>
                </div>
            </div>
        )
    }
}

export default Overlay;
