import React from "react";
import "./Overlay.css";
import $ from 'jquery';


Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});


class OverlayFlightForm extends React.Component {
    constructor(props) { 
        super(props);
    }

    componentDidMount() {

        $(document).ready(function () {
            /*==================================================================
            [ Focus Contact2 ]*/
            $('.input2').each(function(){
                $(this).on('blur', function(){
                    if($(this).val().trim() != "") {
                        $(this).addClass('has-val');
                    }
                    else {
                        $(this).removeClass('has-val');
                    }
                })
            })

            /*==================================================================
            [ Validate ]*/
            var name = $('.validate-input input[name="name"]');
            var email = $('.validate-input input[name="email"]');
            var message = $('.validate-input textarea[name="message"]');


            $('.validate-form').on('submit',function(){
                var check = true;

                if($(name).val().trim() == ''){
                    showValidate(name);
                    check=false;
                }


                if($(email).val().trim().match(/^([a-zA-Z0-9_\-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([a-zA-Z0-9\-]+\.)+))([a-zA-Z]{1,5}|[0-9]{1,3})(\]?)$/) == null) {
                    showValidate(email);
                    check=false;
                }

                if($(message).val().trim() == ''){
                    showValidate(message);
                    check=false;
                }

                return check;
            });


            $('.validate-form .input2').each(function(){
                $(this).focus(function(){
                hideValidate(this);
            });
            });

            function showValidate(input) {
                var thisAlert = $(input).parent();

                $(thisAlert).addClass('alert-validate');
            }

            function hideValidate(input) {
                var thisAlert = $(input).parent();

                $(thisAlert).removeClass('alert-validate');
            }
        });

    }

    render() {
        return (
            <div>
                <form className="form validate-form">

                    <div className="row-wrapper">
                        <div className="input-wrapper" data-validate="A/C is required">
                            <input className="input2" type="text" name="A/C"/>
                            <span className="focus-input2" data-placeholder="A/C"></span>
                        </div>
                        <div className="input-wrapper" data-validate="Name is required">
                            <input className="input2" type="text" name="BACKUP"/>
                            <span className="focus-input2" data-placeholder="BACKUP"></span>
                        </div>
                    </div>
                    <div className="wrap-input2 validate-input" data-validate="Name is required">
                        <input className="input2" type="text" name="name"/>
                        <span className="focus-input2" data-placeholder="MISSION"></span>
                    </div>

                    <div className="wrap-input2 validate-input" data-validate="Name is required">
                        <input className="input2 has-val" type="date" name="name" value={new Date().toDateInputValue()}/>
                        <span className="focus-input2" data-placeholder="DATE"></span>
                    </div>
                    <div className="half-col-wrapper">
                        <div className="wrap-input-half validate-input" data-validate="Name is required">
                            <input className="input2" type="text" name="name"/>
                            <span className="focus-input2" data-placeholder="START TIME"></span>
                        </div>
                        <div className="wrap-input-half-right validate-input" data-validate="Name is required">
                            <input className="input2" type="text" name="name"/>
                            <span className="focus-input2" data-placeholder="END TIME"></span>
                        </div>
                    </div>


                    <div className="wrap-input2 validate-input" data-validate = "Valid email is required: ex@abc.xyz">
                        <input className="input2" type="text" name="email"/>
                        <span className="focus-input2" data-placeholder="EMAIL"></span>
                    </div>

                    <div className="wrap-input2 validate-input" data-validate = "Message is required">
                        <textarea className="input2" name="message"></textarea>
                        <span className="focus-input2" data-placeholder="MESSAGE"></span>
                    </div>

                    <div className="container-form-btn">
                        <div className="wrap-form-btn">
                            <div className="form-bgbtn"></div>
                            <button className="form-btn">
                                Add Flight
                            </button>
                        </div>
                    </div>
                </form>

            </div>

        );
    }
}

export default OverlayFlightForm;
