import React from 'react'
import "./Login.css"
import firebase from "./Firebase.js";


function checkInput(input) {
    if (input == "Amelia Earhart") {
        document.getElementsByClassName("pswdForm")[0].hidden = false;
        document.getElementsByClassName("pswdInput")[0].hidden = false;
    }
    else {
        if (document.getElementsByClassName("pswdForm")[0].hidden == false) {
            document.getElementsByClassName("pswdForm")[0].hidden = true;
            document.getElementsByClassName("pswdInput")[0].hidden = true;
        }
    }
}

function toggleSignIn() {
    if (firebase.auth().currentUser) {
      // [START signout]
      firebase.auth().signOut();
      // [END signout]
    } else {
      var email = document.getElementById('email').value;
      var password = document.getElementById('password').value;
      if (email.length < 4) {
        alert('Please enter an email address.');
        return;
      }
      if (password.length < 4) {
        alert('Please enter a password.');
        return;
      }
      // Sign in with email and pass.
      // [START authwithemail]
      firebase.auth().signInWithEmailAndPassword(email, password).catch(function(error) {
        // Handle Errors here.
        var errorCode = error.code;
        var errorMessage = error.message;
        // [START_EXCLUDE]
        if (errorCode === 'auth/wrong-password') {
          alert('Wrong password.');
        } else {
          alert(errorMessage);
        }
        console.log(error);
        document.getElementById('quickstart-sign-in').disabled = false;
        // [END_EXCLUDE]
      });
      // [END authwithemail]
    }
    document.getElementById('quickstart-sign-in').disabled = true;
  }

function LoginForm() {
    //var pilots = []
    //firebase.ref('/pilots').once('name').then(function(snapshot) {
    //    console.log(snapshot.val())
    //});

    return (
        <form className="form">
            <h2 className="userForm">Email</h2>
            <input className="userInput" type="text" onChange={(e)=>{checkInput(e.target.value.trim())}}/>
            <br/>
            <h2 className="pswdForm" >Password</h2>
            <input className="pswdInput" type="password"/>
            <RenderButton/>
        </form> 
    )
}

function RenderButton(props) {
    return (
        <div className="login-form-btn">
                <button className="login-btn" onClick={props.onSubmit}>
                    Next
                </button>
        </div>
    );
}

class Login extends React.Component{
    constructor(props) {
        super(props)
    }
    render() {
        return (
            <LoginForm/>
        )
    }
}

export default Login;
