import React from 'react';
import './index.css';
import ADD_LOGO from './img/add.png';

function FlightCard(props) {
    return (
        <div className="flightCard">
            <div className="date">
                <h2>{props.flight}</h2>
                <p>9:00 - 12:00</p>
            </div>
            <div className="ac">
                <div className="acTitle">
                    <h5>AC</h5>
                </div>
                <div className="acText">
                    <h5>Amelia Earheart</h5>
                </div>
            </div>
            <div className="fo">
                <div className="foTitle">
                    <h5>FO</h5>
                </div>
                <div className="foText">
                    <h5>Charles Hindbergh</h5>
                    <h5>D.B. Cooper</h5>
                </div>
            </div>
            <hr/>
        </div>
    );
}

class Dashboard extends React.Component {

    constructor(props) {
        super(props);
    }

    renderUpcomingFlights() {
        var cards = []
        this.props.flights.forEach(flight => {
            cards.push(<FlightCard flight={flight}/>)
        });
        return cards
    }
    render() { 
        return (
            <div>
                <h2>Your Upcoming Flights</h2>
                <div className="upcomingFlights">
                    {this.renderUpcomingFlights()}
                    <img className="addFlightBtn" 
                        src={ADD_LOGO} 
                        title='Add a flight'
                        onClick={()=> {this.props.toggleOverlay()}}/>
                </div>
            </div>

        )
    };
}

export default Dashboard;
