import React from "react";
import "./Form.css";
import firebase from "./Firebase.js";


Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});


class MeetingForm extends React.Component {
    constructor(props) { 
        super(props);
        this.state = {
            title: "", 
            type: "meeting", 
            date: new Date().toDateInputValue(),
            startTime: 0,
            endTime: 0,
            location: "", 
            confLink: "",
            notes: "",
            userid: "",
        }
    }


    onBlur(name, e, type) {
        if (type == 'date') {
            var d = new Date(e.target.value);
            d.setMinutes( d.getMinutes() + d.getTimezoneOffset() );
            this.setState({date: d.toDateInputValue()});
        }
        else if (type == 'number')  {
            this.setState({[name]: e.target.value});
        }
        else {
            this.setState({[name]: e.target.value.trim()});
        }
    }

    componentDidMount() {
        this.setState({userid: (this.props.activeUser[0] + this.props.activeUser.split(' ')[1]).toLowerCase()});
    }


    render() {
        return (
            <div>
                <form className="form validate-form">
                    <div className="wrap-input">
                        <input className={"input"+ (this.state.title !== ""? " has-val": "")} type="text" name="title" required={true}/>
                        <span className="focus-input" data-placeholder="MEETING TITLE"></span>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper">
                            <input className="input has-val" type="date" name="name" 
                                required={true}
                                value={new Date().toDateInputValue()} 
                                onBlur={(e)=>{this.onBlur('startDate',e, 'date')}}/>
                            <span className="focus-input" data-placeholder="DATE"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className={"input"+ (this.state.startTime !== 0 ? " has-val": "")} type="number" name="startTime" required={true}/>
                            <span className="focus-input" data-placeholder="START TIME"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className={"input"+ (this.state.endTime !== 0 ? " has-val": "")} type="number" name="endTime" required={true}/>
                            <span className="focus-input" data-placeholder="END TIME"></span>
                        </div>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper">
                            <input className={"input"+ (this.state.location !== "" ? " has-val": "")} type="text" name="esp"/>
                            <span className="focus-input" data-placeholder="LOCATION"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className={"input"+ (this.state.confLink !== "" ? " has-val": "")} type="text"/>
                            <span className="focus-input" data-placeholder="CONFERENCE LINK"></span>
                        </div>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="large-wrapper">
                            <input className={"input"+ (this.state.notes !== "" ? " has-val": "")} type="text"/>
                            <span className="focus-input" data-placeholder="NOTES"></span>
                        </div>
                    </div>



                    <div className="container-form-btn">
                        <div className="wrap-form-btn">
                            <div className="form-bgbtn"></div>
                            <button className="form-btn">
                                Add Meeting
                            </button>
                        </div>
                    </div>
                </form>

            </div>

        );
    }
}

export default MeetingForm;
