import React from 'react';
import "./Schedule.css";
import RIGHT_BTN from './img/right-arrow.png';
import LEFT_BTN from './img/left-arrow.png';

var today = new Date();
var dd = String(today.getDate()).padStart(2, '0');
var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
var yyyy = today.getFullYear();

today = mm + '/' + dd + '/' + yyyy;

function date(props) {
    var today = new Date();
    var weekday = today.getDay();
    var day = today.getDate();
    var month = today.getMonth();
    var year = today.getFullYear();
    var days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
    var months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November","December"];
    return (
        <div className='flight-date'>
            <h2>{days[weekday] +", "+months[month]+" "+day+", "+year}</h2>
        </div>
    )
}

function renderDayHeader(props) {
    return (
        <div className="day-header">
            <img className='date-btn' src={LEFT_BTN}></img>
            {date()}
            <img className='date-btn' src={RIGHT_BTN}></img>
        </div>
    )
}

function Day(props) {
    var headers = ['ETD', 'AC', 'CREW', 'MISSION', 'DETAILS', 'CONFIG', 'REMARKS', 'SQ PRI', 'DCO', 'ETR'];
    var tableHeaders = [];
    var ac = <div className='ac-wrap'>462</div>;
    var dot = <img className='dco-icon' src={OK}/>;
    headers.forEach(title => {
        tableHeaders.push(<th>{title}</th>)
    });
    var data = [['0900', ac, 'Amelia E | Charlies L', '427 AU H102 G00', 'SME', 'DC6S', "", '1 SOA', dot, '1030'], 
                ['2520', ac, 'D.B. Cooper | Maverick', '427 AU H105 G00', 'ALP1', 'DC6S', "", '3 SOA', dot, '1230']]
    var tableRows = [];

    data.forEach(row => {
        var r = [];
        for (var i = 0; i<row.length; i++) {
            r.push(<td className={headers[i].toLowerCase()}>{row[i]}</td>);
        }  
        var hr = parseInt(row[0][0]+row[0][1]);
        var min = parseInt(row[0][2]+row[0][3]);
        tableRows.push(<tr className={(props.date.getHours() > hr) || (hr == props.date.getHours() && props.date.getMinutes() > min) ? 'expired':''}>{r}</tr>);
    });
    return (
        <div className="day-container">
            <table>
                {tableRows}
            </table>
        </div>

    )
}

function Gridline(props) {
    return (
        <div className="gridline">
            <h5 className='hour'>{props.hour+'00'}</h5>
            <div className="line"/>
        </div>
    )
}

function Grid(props) {
    var lines = [];
    for (var i = 2; i < 24 ; i += 2) {
        lines.push(<Gridline hour={i < 10 ? '0'+i.toString() : i.toString()}/>)
    }
    return (
        <div className="grid-container">
            {lines}
        </div>
    )
}


class Schedule extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
        }
    }
    render() {
        return (
            <div className='page-container'>
                {renderDayHeader()}
                <Grid/>
                <Day/>
            </div>
        )
    }
}

export default Schedule;
