import React from "react";
import "./Form.css";
import firebase from "./Firebase.js";
import {firebaseCreateDate, firebaseGetAllPilotIDs} from "./FirebaseHelper.js";
import {RenderSuccessDialog} from "./Overlay.js";

Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});

const fields = [[{name: "date", custom: true, size: 'medium'}, 
{name: "startTime", displayName: "est. departure", size: 'medium'}, 
{name: "endTime", displayName: "est. return", size: 'medium'}, 
{name: "flighttime", custom: true}],

[{name: "captain", select: 'pilots', size: 'medium'}, 
{name: "fo", select: 'pilots', displayName: "first officer", size: 'medium'}, 
{name: "fe", select: 'pilots', displayName: "flight engineer", size: 'medium'}, 
{name: "crew", select: 'pilots', displayName: "other crew", size: 'medium'}], 

[{name: "ac", size: 'small'}, 
{name: "backup", size: 'small'}, 
{name: "mission", size: 'medium'}, 
{name: "details", size: 'medium'}, 
{name: "config", size: 'medium'}], 
[{name: "remarks", size: 'large'}], 
[{name: "pri", displayName: 'sqn pri', size: 'medium'}, 
{name: "dco", select: 'dcolist', size: 'medium'}] ];

const dcolist = ["DCO", "DPCO", "DNCO"];

function RenderButton(props) {
    return (
        <div className="container-form-btn">
            <div className="wrap-form-btn">
                <div className="form-bgbtn"></div>
                <button className="form-btn" onClick={props.onSubmit}>
                    Add Flight
                </button>
            </div>
        </div>
    );
}

function getOptionsList(id, values) {
    //Creates a datalist object for dropdown options
    var l = [];
    console.log(values);
    values.forEach((v) => {
        l.push(<option value={v}/>);
    });
    return (<datalist id={id}>{l}</datalist>);
}



class FlightForm extends React.Component {
    constructor(props) { 
        super(props);
        this.state = {
            date: new Date().toDateInputValue(),
            startTime: "",
            endTime: "",
            captain: "",
            fo: "",
            fe: "",
            crew: "",
            ac: "",
            backup: "",
            mission: "",
            details: "",
            config: "",
            remarks: "",
            pri: "",
            meal: "",
            dco: "",
            type: "flight",
            pilotsList: [],
        }
    }

    firebaseSetBool(pilot) {
        var flightDate = 'flights.' + this.state.date;
        firebase.firestore().collection("pilots").doc(pilot).update({[flightDate]: true})
        .then(function() {
            console.log("Boolean successfully set for pilot");})
        .catch(function(error) {
            console.error("Error setting flight boolean", error);
        })
    }


    onSubmit() { 
        var ok = true;
        var remove = document.getElementsByClassName('alert-validate');
        for (let item of remove) {
            item.classList.remove('alert-validate');
        }
        var required = ['captain', 'fo', 'ac', 'mission'];
        required.forEach(field => {
            if (this.state[field] == "") {
                this.props.flag(field, "Required");
                ok = false;
            }
        })

        if (isNaN(this.state.startTime) || !this.props.timeValid(this.state.startTime)) {
            this.props.flag('startTime', 'Enter between 0000 and 2359');
            ok = false;
        }
        if (isNaN(this.state.endTime) || !this.props.timeValid(this.state.endTime)) {
            this.props.flag('endTime', 'Enter between 0000 and 2359');
            ok = false;
        }
        if (parseInt(this.state.startTime) >= parseInt(this.state.endTime)) {
            this.props.flag('startTime', 'Return must be after dep.');
            ok = false;
        }
        if (!(["DCO", "DNCO", "DPCO"].includes(this.state.dco.toUpperCase()))) {
            this.props.flag('dco', 'Must be DCO/DNCO/DPCO');
            ok = false;
        }
        if (!["TASK", '1', '2', '3', '4', '5', '6'].includes(this.state.pri.toUpperCase())) {
            this.props.flag('pri', 'Must be task or 1-6');
        }
        if (ok) {
            var newEvent = firebase.firestore().collection("events").doc();
            newEvent.set(this.state)
            .then(function() {
                console.log("Event successfully added to events!");})
            .catch(function(error) {
                console.error("Error adding event: ", error);
                ok = false;
            });

            this.firebaseSetBool(this.state.captain);
            this.firebaseSetBool(this.state.fo);
            var updates = {};
            updates['events.'+this.state.captain] = firebase.firestore.FieldValue.arrayUnion({'type':'flight', 'startTime': this.state.startTime, 'endTime': this.state.endTime, 'id':newEvent.id});
            updates['events.'+this.state.fo] = firebase.firestore.FieldValue.arrayUnion({'type':'flight', 'startTime': this.state.startTime, 'endTime': this.state.endTime, 'id':newEvent.id});
            var newUpcomingEvents = [   firebase.firestore().collection('pilots').doc(this.state.captain).collection('upcoming-events').doc(),
                                        firebase.firestore().collection('pilots').doc(this.state.fo).collection('upcoming-events').doc()];

            if (this.state.fe) {
                updates['events.'+this.state.fe] = firebase.firestore.FieldValue.arrayUnion({'type':'flight', 'startTime': this.state.startTime, 'endTime': this.state.endTime, 'id':newEvent.id});
                newUpcomingEvents.push(firebase.firestore().collection('pilots').doc(this.state.fe).collection('upcoming-events').doc());
                this.firebaseSetBool(this.state.fe);
            }
            if (this.state.crew) {
                updates['events.'+this.state.crew] = firebase.firestore.FieldValue.arrayUnion({'type':'flight', 'startTime': this.state.startTime, 'endTime': this.state.endTime, 'id':newEvent.id});
                newUpcomingEvents.push(firebase.firestore().collection('pilots').doc(this.state.crew).collection('upcoming-events').doc());
                this.firebaseSetBool(this.state.crew);
            }
            let date = this.state.date;
            firebase.firestore().collection("dates").doc(date).update(updates)
            .then(function() {
                console.log("Event successfully added to dates!");})
            .catch(function(error) {
                firebaseCreateDate(date);
            })


            newUpcomingEvents.forEach((x)=>{x.set({
                ac: this.state.ac,
                captain: this.state.captain,
                flightid: newEvent.id,
                config: this.state.config,
                date: this.state.date,
                fo: this.state.fo,
                fe: this.state.fe,
                crew: this.state.crew,
                details: this.state.details,
                endTime: this.state.endTime,
                startTime: this.state.startTime,})
            .then(function() {
                console.log("Event successfully added to pilots!");})
            .catch(function(error) {
                console.error("Error writing document: ", error);
                ok = false;
            })})

            if (ok) {
                //window.location.reload();
                document.getElementsByClassName("success-modal")[0].style.display = "block";
            }
        }
    }

    renderField(field) {
        //TODO: fix datalist to be dynamic
        const name = field.name;
        const displayName = field.hasOwnProperty("displayName") ? field.displayName.toUpperCase() : field.name.toUpperCase();
        const wrapperClassName = field.size + "-wrapper";
        const fieldClassName = "input"+ (this.state[name] != "" ? " has-val": "");
        const datalist = field.hasOwnProperty('select') ? field.select : '';
        console.log(this.state.pilotsList)
        return (
            <div id={name} className={wrapperClassName + " validate-input"} onFocus={(e)=>{e.target.parentElement.classList.remove("alert-validate")}}>

            {getOptionsList('pilots', this.state.pilotsList)}
            {getOptionsList('dcolist', dcolist)}

            <input className={fieldClassName} type="text" name={name} list={datalist} onBlur={(e)=>{this.setState({[name]: e.target.value.trim()});}}/>
            <span className="focus-input" data-placeholder={displayName}></span>
            </div>

        )
    }

    renderCustomField(field) {
        switch(field.name) {
            case "flighttime": 

                var [start, end] = [this.state.startTime, this.state.endTime];
                var time = "0 HRS 0 MIN";
                var hours = parseInt(end.substring(0, 2))- parseInt(start.substring(0, 2));
                var minutes = parseInt(end.substring(2, 4))- parseInt(start.substring(2, 4));

                if (hours < 0) {
                    hours += 24
                }
                if (minutes < 0) {
                    minutes += 60;
                    hours -= 1;
                }
                if (minutes >= 60) {
                    minutes -= 60;
                    hours += 1;
                }
                if (hours && minutes && hours >= 0 && minutes >= 0){ 
                    time = hours + " HRS " + minutes + " MIN";
                }
                else {
                    time = "0 HRS 0 MIN";
                }
                return (<div>
                    <input className='input has-val' type="text" disabled={true} value={time}/>
                </div>);
            case "pri": 
                /*return (<div className="small-wrapper">
                    <input className='input' required={true} type="text"/> 
                    <span className="focus-input" data-placeholder="SQN PRI"></span>
                </div>);*/

            case "date": 
                return (<div className="medium-wrapper">
                <input className="input has-val validate-input" type="date" name="name" defaultValue={new Date().toDateInputValue()} onBlur={(e)=>{var d = new Date(e.target.value);
                    d.setMinutes( d.getMinutes() + d.getTimezoneOffset() );
                    this.setState({date: d.toDateInputValue()});}}/>
                <span className="focus-input" data-placeholder="DATE"></span>
            </div>)
            default: 
                const name = field.name;
                const displayName = field.hasOwnProperty("displayName") ? field.displayName.toUpperCase() : field.name.toUpperCase();
                const fieldClassName = "input validate-input"+ (this.state[name] != ""? " has-val": "")
                return (<div id={name} className={field.size + "-wrapper"}>
                    <input className={fieldClassName} autoComplete="new-password" required={field.required} type="number" name={name} onBlur={(e)=>{this.setState({[name]: e.target.value.trim()});}}/>
                    <span className="focus-input" data-placeholder={displayName}></span>
                </div>);
        }
    }

    renderRow(row) {
        var fields = [];
        row.forEach(field => {
            if (field.hasOwnProperty('custom')) {
                fields.push(this.renderCustomField(field));
            }
            else {
                fields.push(this.renderField(field));
            }

        });
        return (
            <div className="row-wrapper"> 
                {fields}
            </div>
        )
    }

    renderForm() {
        var rows = []
        fields.forEach(row => {
            rows.push(this.renderRow(row));
        });
        return (
            <div>
                <form className="form" autoComplete="off"  onSubmit={(e)=>{e.preventDefault(); this.onSubmit()}}>
                    {rows}
                    <RenderSuccessDialog/>
                    <RenderButton/>
                </form>
            </div>
        );
    }

    firebaseGetAllPilotIDs() {
        var pilots = [];
        firebase.firestore().collection("pilots")
            .get()
            .then((querySnapshot) => {
                querySnapshot.forEach((pilot)=> {
                    if (pilot.data().displayName != null) {
                        pilots.push(pilot.id)
                    }
                })
                this.setState({pilotsList: pilots});
            })
            .catch(function(error) {
                console.log("Error getting all pilots: ", error);
            });

    }

    componentDidMount() {
        firebaseGetAllPilotIDs();
    }


    render() {
        return (
            <div>
                {this.renderForm()}
            </div>
        );
    }
}

export default FlightForm;
