import React from"react";
import"./Form.css";
import $ from 'jquery';


Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});


class EventForm extends React.Component {
    constructor(props) { 
        super(props);
    }



    render() {
        return (
            <div>
                <form className="event-form">
                    <div className="large-wrapper" data-validate="Name is required">
                        <input className="input" type="text" name="event-title"/>
                        <span className="focus-input" data-placeholder="EVENT TITLE"></span>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper" data-validate="Date is required">
                            <input className="input has-val" type="date" name="event-date" value={new Date().toDateInputValue()}/>
                            <span className="focus-input" data-placeholder="START DATE"></span>
                        </div>
                        <div className="medium-wrapper" data-validate="Start time is required">
                            <input className="input" type="text" name="event-start"/>
                            <span className="focus-input" data-placeholder="START TIME"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className="input has-val" type="date" name="name" value={new Date().toDateInputValue()}/>
                            <span className="focus-input" data-placeholder="END DATE"></span>
                        </div>
                        <div className="medium-wrapper" data-validate="ESP is required">
                            <input className="input" type="text" name="esp"/>
                            <span className="focus-input" data-placeholder="END TIME"></span>
                        </div>
                    </div>

                    <div className="wrap-input" data-validate="Name is required">
                        <input className="input" type="text" name="remarks"/>
                        <span className="focus-input" data-placeholder="EVENT NOTES"></span>
                    </div>


                    <div className="container-form-btn">
                        <div className="wrap-form-btn">
                            <div className="form-bgbtn"></div>
                            <button className="form-btn">
                                Add Meeting
                            </button>
                        </div>
                    </div>
                </form>

            </div>

        );
    }
}

export default EventForm;
