import React from"react";
import"./Form.css";
import firebase from "./Firebase.js";


Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});


class EventForm extends React.Component {
    constructor(props) { 
        super(props);
        this.state = {
            title: "", 
            type: "apt", 
            startDate: "",
            endDate: "",
            startTime: 0,
            endTime: 0,
            notes: "",
        }
    }

    onSubmit() {
        firebase.firestore().collection("events").doc().set(this.state)
        .then(function() {
            console.log("Event successfully added!");
        })
        .catch(function(error) {
            console.error("Error adding event: ", error);
        });
    }

    onBlur(name, e, type) {
        if (type == 'date') {
            var d = new Date(e.target.value);
            d.setMinutes( d.getMinutes() + d.getTimezoneOffset() );
            this.setState({[name]: d.toDateInputValue()});
        }
        else if (type == 'number')  {
            this.setState({[name]: e.target.value});
        }
        else {
            this.setState({[name]: e.target.value.trim()});
        }
    }


    render() {
        return (
            <div>
                <form className="event-form">
                    <div className="large-wrapper">
                        <input className="input" type="text" name="title" required={true} onBlur={(e)=>{this.onBlur('title',e)}}/>
                        <span className="focus-input" data-placeholder="EVENT TITLE"></span>
                    </div>
                    <div className="row-wrapper"> 
                        <div className="medium-wrapper">
                            <input className="input has-val" required={true}type="date" name="startDate" value={new Date().toDateInputValue()} onBlur={(e)=>{this.onBlur('startDate',e, 'date')}}/>
                            <span className="focus-input" data-placeholder="START DATE"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className="input" type="number" name="startTime" onBlur={(e)=>{this.onBlur('startTime',e, 'number')}}/>
                            <span className="focus-input" data-placeholder="START TIME"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className="input has-val" type="date" name="endDate" value={new Date().toDateInputValue()} onBlur={(e)=>{this.onBlur('endDate',e, 'date')}}/>
                            <span className="focus-input" data-placeholder="END DATE"></span>
                        </div>
                        <div className="medium-wrapper">
                            <input className="input" type="number" name="esp" onBlur={(e)=>{this.onBlur('endTime',e, 'number')}}/>
                            <span className="focus-input" data-placeholder="END TIME"></span>
                        </div>
                    </div>

                    <div className="wrap-input">
                        <input className="input" type="text" name="remarks" onBlur={(e)=>{this.onBlur('notes',e)}}/>
                        <span className="focus-input" data-placeholder="EVENT NOTES"></span>
                    </div>


                    <div className="container-form-btn">
                        <div className="wrap-form-btn">
                            <div className="form-bgbtn"></div>
                                <button className="form-btn" onClick={this.onSubmit}>
                                    Add Event
                                </button>
                        </div>
                    </div>
                </form>

            </div>

        );
    }
}

export default EventForm;
