import React from "react";
import "./Form.css";
import firebase from "./Firebase.js";


Date.prototype.toDateInputValue = (function() {
    var local = new Date(this);
    local.setMinutes(this.getMinutes() - this.getTimezoneOffset());
    return local.toJSON().slice(0,10);
});

const fields = [[{name: "date", custom: true, size: 'medium', required: true}, 
{name: "esd", displayName: "est. departure", size: 'medium', required: true}, 
{name: "esr", displayName: "est. return", size: 'medium', required: true}, 
{name: "flighttime", custom: true}],

[{name: "captain", size: 'medium', required: false}, 
{name: "fo", displayName: "first officer", size: 'medium', required: false}, 
{name: "fe", displayName: "flight engineer", size: 'medium', required: false}, 
{name: "othercrew", displayName: "other crew", size: 'medium', required: false}], 

[{name: "ac", size: 'small', required: false}, 
{name: "backup", size: 'small', required: false}, 
{name: "mission", size: 'medium', required: false}, 
{name: "details", size: 'medium', required: false}, 
{name: "config", size: 'medium', required: false}], 
[{name: "remarks", size: 'large', required: false}], 
[{name: "sqnpriority", custom: true, }, 
{name: "meals", size: 'medium', required: false},
{name: "dco", size: 'medium', required: false}] ];

function RenderButton(props) {
    return (
        <div className="container-form-btn">
            <div className="wrap-form-btn">
                <div className="form-bgbtn"></div>
                <button className="form-btn" onClick={props.onSubmit}>
                    Add Flight
                </button>
            </div>
        </div>
    );
}

class FlightForm extends React.Component {
    constructor(props) { 
        super(props);
        this.state = {
            date: new Date().toDateInputValue(),
            timeStart: null,
            timeEnd: null,
            flighttime: null,
            captain: "",
            fo: "",
            fe: "",
            crew: "",
            ac: "",
            backup: "",
            mission: "",
            details: "",
            config: "",
            remarks: "",
            pri: "",
            meal: "",
            dco: ""
        }
    }

    onSubmit() {
        firebase.firestore().collection("events").doc().set(this.state)
        .then(function(flight) {
            console.log("Flight successfully added!", flight.id);
        })
        .catch(function(error) {
            console.error("Error adding flight: ", error);
        });
    }

    renderField(field) {
        const name = field.name;
        const displayName = field.hasOwnProperty("displayName") ? field.displayName.toUpperCase() : field.name.toUpperCase();
        const wrapperClassName = field.size + "-wrapper";
        const fieldClassName = "input"+ (this.state[name] != ""? " has-val": "")
        return (
            <div className={wrapperClassName}>
                <input className={fieldClassName} required={field.required} type="text" name={name} onBlur={(e)=>{this.setState({[name]: e.target.value.trim()});}}/>
                <span className="focus-input" data-placeholder={displayName}></span>
            </div>
        )
    }

    renderCustomField(field) {
        switch(field.name) {
            case "flighttime": 
                var hours = parseInt(String(this.state.esr).substring(0, 2))- parseInt(String(this.state.esd).substring(0, 2));
                var minutes = parseInt(String(this.state.esr).substring(2, 4))- parseInt(String(this.state.esd).substring(2, 4));
                var time = "0 HRS 0 MIN";
                if (hours < 0) {
                    hours += 24
                }
                if (minutes < 0) {
                    minutes += 60;
                    hours -= 1;
                }
                if (minutes >= 60) {
                    minutes -= 60;
                    hours += 1;
                }
                if (hours || minutes){ 
                    time = hours + " HRS " + minutes + " MIN";
                }

                return (<div>
                    <input className='input has-val' type="text" disabled={true} value={time}/>
                </div>);
            case "sqnpriority": 
                return (<div className="small-wrapper">
                    <input className='input' required={true} type="text"/> 
                    <span className="focus-input" data-placeholder="BLAH"></span>
                </div>);
            case "date": 
                return (<div className="medium-wrapper validate-input" data-validate="Date is required">
                <input className="input has-val" type="date" name="name" defaultValue={new Date().toDateInputValue()}/>
                <span className="focus-input" data-placeholder="DATE"></span>
            </div>)
            default: 
                return (<p>Hello</p>);
        }
    }

    renderRow(row) {
        var fields = [];
        row.forEach(field => {
            if (field.hasOwnProperty('custom')) {
                fields.push(this.renderCustomField(field));
            }
            else {
                fields.push(this.renderField(field));
            }

        });
        return (
            <div className="row-wrapper"> 
                {fields}
            </div>
        )
    }

    renderForm() {
        var rows = []
        fields.forEach(row => {
            rows.push(this.renderRow(row));
        });
        return (
            <div>
                <form className="form validate-form" autoComplete="off" onSubmit={(e)=>{e.preventDefault(); this.onSubmit()}}>
                    {rows}
                    <RenderButton/>
                </form>
            </div>
        );
    }

    render() {
        return (
            <div>
                {this.renderForm()}
            </div>
        );
    }
}

export default FlightForm;
