import React from 'react';
import firebase from './Firebase.js';
import "./Schedule.css";
import RIGHT_BTN from './img/right-arrow.png';
import LEFT_BTN from './img/left-arrow.png';

var today = new Date();
var dd = String(today.getDate()).padStart(2, '0');
var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
var yyyy = today.getFullYear();

today = mm + '/' + dd + '/' + yyyy;

function RenderDate(props) {
    var weekday = props.date.getDay();
    var day = props.date.getDate();
    var month = props.date.getMonth();
    var year = props.date.getFullYear();
    var days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    var months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November","December"];

    if (props.view == 'sm-view') {
        return (
            <div className={'flight-date sm-view'}>
                <h2>{month+" / "+day+" / "+year}</h2>
            </div>
        )
    }

    return (
        <div className={'flight-date lg-view'}>
            <h2>{days[weekday] +", "+months[month]+" "+day+", "+year}</h2>
        </div>
    )
}

function RenderDayHeader(props) {
    var displayDate = new Date(props.date);
    var curDate = new Date();
    var diff = Math.round((displayDate.getTime() - curDate.getTime())/(24*60*60*1000));

    // Only show flights for last 3 days to the next 2 weeks
    var disableLeft = diff == -3;
    var disableRight= diff == 14;
    return (
        <div className="day-header">
            <img className={'date-btn' + (disableLeft ? ' disabled': '')} src={LEFT_BTN} onClick={()=>{props.changeDate(disableLeft ? 0 : -1)}}></img>
            <RenderDate date={displayDate} view="lg-view"/>
            <RenderDate date={displayDate} view="sm-view"/>
            <img className={'date-btn' + (disableRight ? ' disabled': '')} src={RIGHT_BTN} onClick={()=>{props.changeDate(disableRight ? 0 : 1)}}></img>
        </div>
    )
}

function Event(props) {
    return (<td><button className={props.name} style={props.style} onClick={props.toggleOverlay}/></td>)
}

function createRow(row, toggleOverlay) {
    var r = [];
    r.push(<td className='pilot-col'>{row[0]}</td>);
    for (var i = 0; i < 12 ; i += 1) {
        r.push(<td></td>);
    }
    row[1].forEach(event => {
        var [name, left, right] = event;
        var offset = 0;
        offset = Math.floor((left%2)/0.5);
        var style = {width: ((right-left)*50)+'%', left: (offset*25)+'%'};
        r[1+parseInt(left/2)] = <Event name={name} style={style} toggleOverlay={()=>{Overlay('flight', '7893521')}}/>;
    })

    return r;
}

function Day(props) {
    var data = [['Amelia Earhart', [['flight', 2, 5], ['flight', 8, 9], ['meeting', 12, 15], ['apt', 20, 21]]], 
    ['D.B. Cooper', [['meeting', 4, 6], ['flight', 10, 14]]], 
    ['Pete Mitchell', [['apt', 8, 9], ['flight', 14, 17], ['meeting', 19, 21]]], 
    ['Goose', [['flight', 5, 8], ['meeting', 9, 10], ['apt', 17, 18]]], 
    ['Maverick', [['apt', 4, 5], ['flight', 12, 14]]], 
    ['Charles Lindbergh', [['meeting', 5, 6], ['apt', 14, 17], ['meeting', 19.5, 22]]], 
    ['Iceman', [['flight', 3, 5], ['flight', 9, 10], ['meeting', 12, 15]]], 
    ['Jenny', [['apt', 5, 6], ['flight', 10, 14]]], 
    ['Pete Mitchell', [['apt', 8, 9], ['flight', 15.5, 17], ['meeting', 19, 21]]], 
    ['Goose', [['flight', 4.25, 8], ['meeting', 9, 10], ['apt', 17.5, 18.75]]], 
    ['Maverick', [['apt', 4, 5], ['flight', 12, 14]]], 
    ['Charles Lindbergh', [['meeting', 5, 6], ['apt', 14, 17], ['meeting', 19, 20]]]];
    var tableRows = [];

    var tableHeaders = [];
    tableHeaders.push(<th></th>);
    for (var i = 0; i < 24 ; i += 2) {
        tableHeaders.push(<th>{(i < 10 ? '0'+i.toString() : i.toString())+'00'}</th>);
    }
    var rowTemplate = [];
    for (var i = 0; i < 12 ; i += 1) {
        rowTemplate.push(<td></td>);
    }

    data.forEach(row => {
        tableRows.push(<tr>{createRow(row, props.toggleOverlay)}</tr>);
    });

    return (
        <div className="day-container">
            <table className='schedule-table'>
                <tr>
                    {tableHeaders}
                </tr>
                {tableRows}
            </table>
        </div>

    )
}

function Legend(props) {
    return (
        <div className="legend-container">
            <div className="legend-color flight"></div>
            <h4 className="legend-entry">Flight</h4>
            <div className="legend-color apt"></div>
            <h4 className="legend-entry">Appointment</h4>
            <div className="legend-color meeting"></div>
            <h4 className="legend-entry">Meeting</h4>
        </div>
    )
}


class Schedule extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            date: new Date(),
        }
    }


    getData(newDate) {
        var date = newDate.getFullYear() + '-' + (newDate.getMonth() < 9 ? '0':'') + (newDate.getMonth()+1) + '-' + (newDate.getDate() < 10 ? '0':'') + (newDate.getDate()) ;
        //get new data
        firebase.firestore().collection("dates").doc(date)
        .get()
        .then((querySnapshot) => {
            if (!querySnapshot.empty) {
                this.setState({data: querySnapshot})
            }
            else {
                console.log("Document doesn't exist");
                this.setState({data: null});
            };
        })
        .catch(function(error) {
            console.log("Error getting documents: ", error);
        });
    }

    changeDate(offset) {
        if (offset != 0) {
            var newDate = new Date(this.state.date);
            newDate.setDate(newDate.getDate() + offset);
            this.setState({date: new Date(newDate)})
            this.getData(newDate);
        }
    }

    render() {
        return (
            <div className='page-container'>
                <RenderDayHeader date={this.state.date} changeDate={(i)=>{this.changeDate(i)}}/>
                <Day toggleOverlay={(event,id)=>{this.props.toggleOverlay(event, id)}}/>
                <Legend/>
            </div>
        )
    }
}

export default Schedule;
