import React from 'react';
import moment from 'moment'
import './Dashboard.css';
import RIGHT_BTN from './img/right-arrow.png';
import LEFT_BTN from './img/left-arrow.png';

function DayHeader(props) {
    return (

        <div className='dayHeader'>
            <h3 id='date'>{props.day + "  "}</h3><h3>{props.date}</h3>
        </div>
    )
}

function DayContents(props) {
    var content = [];
    props.events.forEach(event => {
        var [type, start, end, details] = event;
        content.push(
            <div className={type+'Event'} style={{height: ((end-start)*60)+'px'}}>
                <p>{(start < 10 ? '0' : '')+start*100 + " - " + (end < 10 ? '0' : '')+ end*100}</p>
                <h5 className='name'>{type[0].toUpperCase()+type.slice(1,)}</h5>
                <h6>{details}</h6>
            </div>
        )
    });
    return content;
}

function DayView(props) {
    return (
        <div className='dayView'>
            <DayHeader day={props.day} date={props.date}/>
            <DayContents events={props.events}/>
        </div>
    )
}

function WeekdaysHeader(props) {
    var weekdayShort = moment.weekdaysShort();
    var content = []
    weekdayShort.forEach(day => {
        content.push(<th key={day} className="week-day">
        {day}
       </th>);
    })

    return content;
}



function DaysOfMonth(props) {



    let blanks = [];
    for (let i = 0; i < props.firstDayOfMonth; i++) {
      blanks.push(
        <td className="calendar-day empty">{""}</td>
      );
    }
    let daysInMonth = [];
    for (let d = 1; d <= props.daysInMonth; d++){ 
        var currentDay = week.includes(d) && props.displayed.month() == props.selected.month() ? "curweek" : "";   
            daysInMonth.push(  
               <td key={d} className={`calendar-day ${currentDay}`}>   
                 <span onClick={e => { props.onDayClick(props.selected.date(d));}} >
              {d}
           </span>
               </td>);
     }
    var totalSlots = [...blanks, ...daysInMonth];
    let rows = [];
    let cells = [];

    totalSlots.forEach((row, i) => {
        if (i % 7 !== 0 ) {
          cells.push(row); // if index not equal 7 that means not go to next week
        } else {
          rows.push(cells); // when reach next week we contain all td in last week to rows 
          cells = []; // empty container 
          cells.push(row); // in current loop we still push current row to new container
        }
        if (i === totalSlots.length - 1) { // when end loop we add remain date
          rows.push(cells);
        }
      });

      let daysinmonth = rows.map((d, i) => {
        return <tr>{d}</tr>;
      });

      return (
        <table className="calendar-table">
        <thead>
          <tr>{WeekdaysHeader()}</tr>
        </thead>
        <tbody>{daysinmonth}</tbody>
      </table>
      )
}

class WeekCalendar extends React.Component {

    constructor(props) {
        super(props);
    }

    render () {
        var days = ['Mon', 'Tues', 'Wed', 'Thur', 'Fri'];
        var content = [];
        for (var i=0; i < 5; i++) {
            content.push(<DayView day={days[i]} date={this.props.dates[i]} events={this.props.events[i]}/>)
        }
        return content;
    }
}

class MonthCalendar extends React.Component {
    constructor(props) {

        super(props);
        this.state = {
            curWeek: onDayClick(moment()), 
            curMonth: moment.month(),
            displayed: moment()
        }
    }

    onDayClick = (d) => { 
        var week = [];
        var startOfWeek = d.startOf('week');
        for (var i =0; i < 7; i++) {
            week.push(startOfWeek.date());
            startOfWeek.add(1, 'day');
        }
        this.setState({curWeek: startOfWeek, curMonth: d.month()});
        console.log('changing selected: ', this.state.curWeek, this.state.curMonth);
    };

    firstDayOfMonth = () => {
        let dateObject = this.state.displayed;
        let firstDay = moment(dateObject)
                     .startOf("month")
                     .format("d"); 
       return firstDay;
    };

    month = () => {
        return this.state.displayed.format("MMMM");};
    year = () => {
        return this.state.displayed.format("YYYY");
    }

    onPrev = () => {
        this.setState({displayed: this.state.displayed.subtract(1, 'month')});
        console.log('changing displayed: ', this.state.displayed)
    };
    onNext = () => {
        this.setState({displayed: this.state.displayed.add(1, 'month')});
        console.log('changing displayed: ', this.state.displayed)
    };

    render () {

        var firstDay = this.firstDayOfMonth();
        var daysInMonth = this.state.displayed.daysInMonth();
        return (
        <div>
            <div className="month-calendar">
                <img className='month-btn' src={LEFT_BTN} onClick={e => {this.onPrev();}}></img>
                <div className="month-calendar-header">{this.month() + " " + this.year()}</div>
                <img className='month-btn' src={RIGHT_BTN} onClick={e => {this.onNext();}}></img>
            </div>
            <DaysOfMonth displayed={this.state.displayed} firstDayOfMonth={firstDay} daysInMonth={daysInMonth} onDayClick={(d)=>{this.onDayClick(d)}}/>
        </div>)
    }
}

export default WeekCalendar;
export {MonthCalendar};
