import Login from "./Login";
import React from 'react';
import ReactGA from 'react-ga';
import firebase from "./Firebase.js";
import ErrorPage from './ErrorPage.js';

function initializeReactGA() {
    ReactGA.initialize('UA-108277898-5');
    ReactGA.pageview('/Personnel');
}

function PersonnelInfo(props) {
    return (
        <div>
            <RenderInfo user={props.user}/>
            <RenderLogoutBtn/>
        </div>
    )
}

function RenderInfo(props) {
    return (
        <div>
            <div className="myInfo">
                <div className="info">
                    <h6>UserID</h6>
                    <input id="userid" defaultValue={props.user} type="text"/>
                </div>
                <div className="info">
                    <h6>Name</h6>
                    <input id="name" defaultValue={props.name} type="text"/>
                </div>
            </div>
            <div className="teamInfo">
                <teamInfo/>
            </div>
        </div>
    )
}

function teamInfo() {
    var team = [];

    this.state.team.forEach((member) => {
        team.push(<p>{parseID(member)}</p>)
    }) 

    return team

}

function getTeamMembers(user) {
    firebase.firestore().collection("pilots").doc(user)
    .get()
    .then((querySnapshot) => {
        if (!querySnapshot.empty) {
            var newteam = querySnapshot.data().team;
            firebase.firestore().collection("pilots").doc(newteam.toString())
            .get()
            .then((querySnapshot) => {
                if (!querySnapshot.empty) {
                    this.setState({team: querySnapshot.data().pilots});
                }
                else {
                    console.log("Document doesn't exist");
                    this.setState({team: null});
                };
            });  
        }
        else {
            console.log("Document doesn't exist");
            this.setState({team: null});
        };
    })
    .catch(function(error) {
        console.log("This user doesn't exist: ", error);
    });
}

function RenderLogoutBtn() {
    return (
        <div className='logout-btn-div '>
                <button className="logout-btn" type="button" onClick={logOut}>
                    Log Out
                </button>
        </div>
    );
}

function logOut() {
    if (!window.confirm("Are you sure you want to log out?")) {
        return;
      } 
    return <Login/>
}

function parseID(id) {
    //aearhart to A. Earhart 
    if (id) {
    return id[0].toUpperCase() + ". " + id[1].toUpperCase() + id.slice(2);}
}

function parseName(name) {
    //Amelia Earhart to aearhart
    if (name) {
    return name[0].toLowerCase() + name.split(name.indexOf(' '));}
}


class Personnel extends React.Component{
    constructor(props) {
        super(props)
        /* name */
        this.state={
            team: [],
            userID: '',
            hasError: false
        };
    }
    componentDidCatch(error, info) {
        // Display fallback UI
        this.setState({ hasError: true });
        console.log(error, info);
    }

    componentDidMount() {
        var name = parseName(this.props.name);
        initializeReactGA();
        this.setState({userID: name});
        console.log(name)
        getTeamMembers(this.state.userID);
    }

    render() {
        if (this.state.hasError) {
            return <ErrorPage/>
        }
        return (
            <PersonnelInfo user={this.state.userID} name={this.props.name}/>
        )
    }
}

export default Personnel;
