import React from 'react';
import './index.css';
import ADD_LOGO from './img/add.png';

function FlightCard(props) {
    return (
        <div className="flightCard">
            <div className="date">
                <h2>{props.flight}</h2>
                <p>9:00 - 12:00</p>
            </div>
            <hr/>
            <div className="infoLine">
                <div className="title">
                    <h5>CAPT</h5>
                </div>
                <div className="text">
                    <h5>Amelia Earheart</h5>
                </div>
            </div>
            <div className="infoLine">
                <div className="title">
                    <h5>FO</h5>
                </div>
                <div className="text">
                    <h5>Charles Hindbergh</h5>
                    <h5>D.B. Cooper</h5>
                </div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>A/C</h5></div>
                <div className="text"><h5>483</h5></div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>TYPE</h5></div>
                <div className="text"><h5>ALP 1</h5></div>
            </div>
            <div className="infoLine">
                <div className="title"><h5>CONFIG</h5></div>
                <div className="text"><h5>DC6S</h5></div>
            </div>
        </div>
    );
}

class Dashboard extends React.Component {

    constructor(props) {
        super(props);
    }

    renderUpcomingEvents() {
        return (
            <div className="upcomingEvents">
                <div className="weekView">
                    {this.renderWeek()}
                </div>
                <div className="monthView">
                    {this.renderMonth()}
                </div>
            </div>
        )
    }

    renderUpcomingFlights() {
        var cards = []
        this.props.flights.forEach(flight => {
            cards.push(<FlightCard flight={flight}/>)
        });
        return cards
    }
    render() { 
        return (
            <div>
                <h2>My Upcoming Flights</h2>
                <div className="upcomingFlights">
                    {this.renderUpcomingFlights()}
                </div>
                <h2>My Schedule</h2>
                <div className="upcomingEvents">
                    {this.renderUpcomingEvents()}
                </div>
            </div>

        )
    };
}

export default Dashboard;
